src/app/tools/property-comparison-tool/p.content:
'use client';

import { useState } from 'react';
import Footer from '@/components/footer/Footer';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';

import { Calculator, Plus, Trash2, BookOpen, BarChart3, Home, DollarSign } from 'lucide-react';

interface Property {
  id: string;
  name: string;
  price: number;
  squareFootage: number;
  bedrooms: number;
  bathrooms: number;
  yearBuilt: number;
  propertyType: string;
  location: string;
  monthlyRent: number;
  expenses: number;
  downPayment: number;
  loanAmount: number;
  interestRate: number;
  loanTerm: number;
}

interface ComparisonMetrics {
  pricePerSqFt: number;
  monthlyPayment: number;
  totalCashRequired: number;
  monthlyNetCashFlow: number;
  capRate: number;
  cashOnCashReturn: number;
  totalReturn: number;
}

export default function PropertyComparisonTool() {
  const [properties, setProperties] = useState<Property[]>([
    {
      id: '1',
      name: 'Property 1',
      price: 500000,
      squareFootage: 2000,
      bedrooms: 3,
      bathrooms: 2,
      yearBuilt: 2010,
      propertyType: 'single-family',
      location: 'Suburban',
      monthlyRent: 3500,
      expenses: 1200,
      downPayment: 100000,
      loanAmount: 400000,
      interestRate: 7.5,
      loanTerm: 30
    },
    {
      id: '2',
      name: 'Property 2',
      price: 650000,
      squareFootage: 2500,
      bedrooms: 4,
      bathrooms: 3,
      yearBuilt: 2015,
      propertyType: 'single-family',
      location: 'Urban',
      monthlyRent: 4200,
      expenses: 1500,
      downPayment: 130000,
      loanAmount: 520000,
      interestRate: 7.25,
      loanTerm: 30
    }
  ]);

  const addProperty = () => {
    const newProperty: Property = {
      id: Date.now().toString(),
      name: `Property ${properties.length + 1}`,
      price: 500000,
      squareFootage: 2000,
      bedrooms: 3,
      bathrooms: 2,
      yearBuilt: 2010,
      propertyType: 'single-family',
      location: 'Suburban',
      monthlyRent: 3500,
      expenses: 1200,
      downPayment: 100000,
      loanAmount: 400000,
      interestRate: 7.5,
      loanTerm: 30
    };
    setProperties([...properties, newProperty]);
  };

  const removeProperty = (id: string) => {
    setProperties(properties.filter(p => p.id !== id));
  };

  const updateProperty = (id: string, field: keyof Property, value: string | number) => {
    setProperties(properties.map(p => 
      p.id === id ? { ...p, [field]: value } : p
    ));
  };

  const calculateMetrics = (property: Property): ComparisonMetrics => {
    const monthlyRate = property.interestRate / 100 / 12;
    const numberOfPayments = property.loanTerm * 12;

    const monthlyPayment = property.loanAmount * 
      (monthlyRate * Math.pow(1 + monthlyRate, numberOfPayments)) / 
      (Math.pow(1 + monthlyRate, numberOfPayments) - 1);

    const pricePerSqFt = property.price / property.squareFootage;
    const totalCashRequired = property.downPayment + (property.price * 0.03); // Including closing costs
    const monthlyNetCashFlow = property.monthlyRent - property.expenses - monthlyPayment;
    const annualNetIncome = (property.monthlyRent - property.expenses) * 12;
    const capRate = (annualNetIncome / property.price) * 100;
    const annualCashFlow = monthlyNetCashFlow * 12;
    const cashOnCashReturn = (annualCashFlow / totalCashRequired) * 100;
    const totalReturn = capRate + (property.price * 0.03) / property.price * 100; // Assuming 3% appreciation

    return {
      pricePerSqFt,
      monthlyPayment,
      totalCashRequired,
      monthlyNetCashFlow,
      capRate,
      cashOnCashReturn,
      totalReturn
    };
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
