import { useState, useCallback } from 'react';

export interface ClosingCostBreakdown {
  buyerCosts: {
    loanOriginationFee: number;
    appraisalFee: number;
    homeInspection: number;
    titleInsurance: number;
    titleSearch: number;
    attorneyFees: number;
    surveyFee: number;
    creditReport: number;
    floodCertification: number;
    taxService: number;
    underwritingFee: number;
    processingFee: number;
    wireFee: number;
    recordingFees: number;
    transferTaxes: number;
    prepaidInterest: number;
    homeownersInsurance: number;
    propertyTaxes: number;
    escrowDeposit: number;
    total: number;
  };
  sellerCosts: {
    realEstateCommission: number;
    titleInsurance: number;
    attorneyFees: number;
    transferTaxes: number;
    recordingFees: number;
    surveyFee: number;
    homeWarranty: number;
    repairs: number;
    prorations: number;
    payoffFees: number;
    total: number;
  };
  totalClosingCosts: number;
  percentageOfPurchasePrice: number;
}

export const useClosingCostsCalculator = () => {
  const [purchasePrice, setPurchasePrice] = useState<number>(400000);
  const [loanAmount, setLoanAmount] = useState<number>(320000);
  const [propertyType, setPropertyType] = useState<string>("single-family");
  const [state, setState] = useState<string>("california");
  const [transactionType, setTransactionType] = useState<string>("purchase");

  const [loanType, setLoanType] = useState<string>("conventional");
  const [downPaymentPercent, setDownPaymentPercent] = useState<number>(20);
  const [interestRate, setInterestRate] = useState<number>(7.0);
  const [loanTerm, setLoanTerm] = useState<number>(30);

  const [customOriginationFee, setCustomOriginationFee] = useState<number>(0);
  const [customAppraisalFee, setCustomAppraisalFee] = useState<number>(0);
  const [customInspectionFee, setCustomInspectionFee] = useState<number>(0);
  const [customTitleInsurance, setCustomTitleInsurance] = useState<number>(0);
  const [customAttorneyFees, setCustomAttorneyFees] = useState<number>(0);
  const [customCommissionRate, setCustomCommissionRate] = useState<number>(6.0);

  const [results, setResults] = useState<ClosingCostBreakdown | null>(null);

  const calculateClosingCosts = useCallback(() => {
    const downPayment = purchasePrice * (downPaymentPercent / 100);
    const calculatedLoanAmount = purchasePrice - downPayment;
    const actualLoanAmount = loanAmount || calculatedLoanAmount;

    const buyerCosts = {
      loanOriginationFee: customOriginationFee || actualLoanAmount * 0.005,
      appraisalFee: customAppraisalFee || (propertyType === "condo" ? 450 : 550),
      homeInspection: customInspectionFee || (propertyType === "condo" ? 350 : 500),
      titleInsurance: customTitleInsurance || purchasePrice * 0.005,
      titleSearch: 200,
      attorneyFees: customAttorneyFees || (state === "new-york" ? 1500 : 800),
      surveyFee: propertyType === "condo" ? 0 : 400,
      creditReport: 50,
      floodCertification: 25,
      taxService: 75,
      underwritingFee: loanType === "fha" ? 800 : 500,
      processingFee: 400,
      wireFee: 30,
      recordingFees: 125,
      transferTaxes: purchasePrice * (state === "new-york" ? 0.004 : 0.001),
      prepaidInterest: ((actualLoanAmount * (interestRate / 100)) / 365) * 15,
      homeownersInsurance: purchasePrice * 0.0035,
      propertyTaxes: (purchasePrice * 0.012) / 2,
      escrowDeposit: (purchasePrice * 0.0035) + ((purchasePrice * 0.012) / 2),
    };
    const buyerTotal = Object.values(buyerCosts).reduce((acc, cost) => acc + cost, 0);

    const sellerCosts = {
      realEstateCommission: purchasePrice * (customCommissionRate / 100),
      titleInsurance: purchasePrice * 0.003,
      attorneyFees: customAttorneyFees || 800,
      transferTaxes: purchasePrice * (state === "california" ? 0.0011 : 0.001),
      recordingFees: 75,
      surveyFee: 0,
      homeWarranty: 500,
      repairs: purchasePrice * 0.01,
      prorations: ((purchasePrice * 0.012) / 12) * 3,
      payoffFees: 300,
    };
    const sellerTotal = Object.values(sellerCosts).reduce((acc, cost) => acc + cost, 0);

    const totalClosingCosts =
      transactionType === "purchase"
        ? buyerTotal
        : transactionType === "sale"
        ? sellerTotal
        : buyerTotal + sellerTotal;

    setResults({
      buyerCosts: { ...buyerCosts, total: buyerTotal },
      sellerCosts: { ...sellerCosts, total: sellerTotal },
      totalClosingCosts,
      percentageOfPurchasePrice: (totalClosingCosts / purchasePrice) * 100,
    });
  }, [
    purchasePrice,
    loanAmount,
    propertyType,
    state,
    transactionType,
    loanType,
    downPaymentPercent,
    interestRate,
    customOriginationFee,
    customAppraisalFee,
    customInspectionFee,
    customTitleInsurance,
    customAttorneyFees,
    customCommissionRate,
  ]);

  return {
    purchasePrice, setPurchasePrice,
    loanAmount, setLoanAmount,
    propertyType, setPropertyType,
    state, setState,
    transactionType, setTransactionType,
    loanType, setLoanType,
    downPaymentPercent, setDownPaymentPercent,
    interestRate, setInterestRate,
    loanTerm, setLoanTerm,
    customOriginationFee, setCustomOriginationFee,
    customAppraisalFee, setCustomAppraisalFee,
    customInspectionFee, setCustomInspectionFee,
    customTitleInsurance, setCustomTitleInsurance,
    customAttorneyFees, setCustomAttorneyFees,
    customCommissionRate, setCustomCommissionRate,
    results,
    calculateClosingCosts,
  };
};

src/app/tools/investment-growth-simulato.content:
'use client'

import { useState } from 'react'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs'
import { Separator } from '@/components/ui/separator'
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts'
import { TrendingUp, Calculator, PieChart as PieChartIcon, BarChart3, Target, Lightbulb, AlertTriangle, BookOpen } from 'lucide-react'

interface InvestmentInputs {
  property: {
    purchasePrice: number
    downPayment: number
    closingCosts: number
    renovationCosts: number
    propertyType: string
    location: string
  }
  financing: {
    loanAmount: number
    interestRate: number
    loanTerm: number
    mortgageType: string
  }
  rental: {
    monthlyRent: number
    rentGrowthRate: number
    vacancyRate: number
    managementFee: number
  }
  expenses: {
    propertyTaxes: number
    insurance: number
    maintenance: number
    repairs: number
    utilities: number
    other: number
    expenseGrowthRate: number
  }
  assumptions: {
    appreciationRate: number
    inflationRate: number
    taxRate: number
    capitalGainsRate: number
    holdingPeriod: number
    sellingCosts: number
  }
}

interface YearlyProjection {
  year: number
  propertyValue: number
  totalEquity: number
  annualRent: number
  totalExpenses: number
  netCashFlow: number
  cumulativeCashFlow: number
  totalReturn: number
  annualizedReturn: number
  mortgageBalance: number
  principalPaydown: number
  taxBenefits: number
}

interface InvestmentSummary {
  totalInvestment: number
  totalCashFlow: number
  totalAppreciation: number
  totalTaxBenefits: number
  totalReturn: number
  annualizedReturn: number
  cashOnCashReturn: number
  capRate: number
  breakEvenYear: number
  projections: YearlyProjection[]
  scenarios: {
    conservative: YearlyProjection[]
    moderate: YearlyProjection[]
    optimistic: YearlyProjection[]
  }
}

export default function InvestmentGrowthSimulator() {
  const [inputs, setInputs] = useState<InvestmentInputs>({
    property: {
      purchasePrice: 300000,
      downPayment: 60000,
      closingCosts: 9000,
      renovationCosts: 15000,
      propertyType: 'single-family',
      location: 'suburban'
    },
    financing: {
      loanAmount: 240000,
      interestRate: 6.5,
      loanTerm: 30,
      mortgageType: 'fixed'
    },
    rental: {
      monthlyRent: 2500,
      rentGrowthRate: 3,
      vacancyRate: 5,
      managementFee: 8
    },
    expenses: {
      propertyTaxes: 3600,
      insurance: 1200,
      maintenance: 1800,
      repairs: 1200,
      utilities: 0,
      other: 600,
      expenseGrowthRate: 2.5
    },
    assumptions: {
      appreciationRate: 4,
      inflationRate: 2.5,
      taxRate: 25,
      capitalGainsRate: 15,
      holdingPeriod: 10,
      sellingCosts: 6
    }
  })

  const [results, setResults] = useState<InvestmentSummary | null>(null)
  const [selectedScenario, setSelectedScenario] = useState<'conservative' | 'moderate' | 'optimistic'>('moderate')

  const calculateProjections = () => {
    const scenarios = {
      conservative: { appreciation: inputs.assumptions.appreciationRate - 1, rentGrowth: inputs.rental.rentGrowthRate - 1 },
      moderate: { appreciation: inputs.assumptions.appreciationRate, rentGrowth: inputs.rental.rentGrowthRate },
      optimistic: { appreciation: inputs.assumptions.appreciationRate + 1, rentGrowth: inputs.rental.rentGrowthRate + 1 }
    }

    const calculateScenario = (appreciationRate: number, rentGrowthRate: number): YearlyProjection[] => {
      const projections: YearlyProjection[] = []
      const monthlyPayment = calculateMonthlyPayment(inputs.financing.loanAmount, inputs.financing.interestRate, inputs.financing.loanTerm)

      let currentPropertyValue = inputs.property.purchasePrice
      let currentRent = inputs.rental.monthlyRent
      let currentExpenses = inputs.expenses.propertyTaxes + inputs.expenses.insurance + inputs.expenses.maintenance + 
                           inputs.expenses.repairs + inputs.expenses.utilities + inputs.expenses.other
      let mortgageBalance = inputs.financing.loanAmount
      let cumulativeCashFlow = 0

      for (let year = 1; year <= inputs.assumptions.holdingPeriod; year++) {
        // Property appreciation
        currentPropertyValue *= (1 + appreciationRate / 100)

        // Rent growth
        currentRent *= (1 + rentGrowthRate / 100)

        // Expense growth
        currentExpenses *= (1 + inputs.expenses.expenseGrowthRate / 100)

        // Annual rent after vacancy and management
        const effectiveRent = currentRent * 12 * (1 - inputs.rental.vacancyRate / 100)
        const managementCost = effectiveRent * (inputs.rental.managementFee / 100)
        const annualRent = effectiveRent - managementCost

        // Total annual expenses including mortgage
        const totalExpenses = currentExpenses + (monthlyPayment * 12)

        // Net cash flow
        const netCashFlow = annualRent - totalExpenses
        cumulativeCashFlow += netCashFlow

        // Mortgage balance and principal paydown
        const yearlyInterest = mortgageBalance * (inputs.financing.interestRate / 100)
        const yearlyPrincipal = (monthlyPayment * 12) - yearlyInterest
        mortgageBalance -= yearlyPrincipal

        // Tax benefits (depreciation + interest deduction)
        const depreciation = inputs.property.purchasePrice * 0.0364 // 27.5 years for residential
        const taxBenefits = (depreciation + yearlyInterest) * (inputs.assumptions.taxRate / 100)

        // Total equity
        const totalEquity = currentPropertyValue - mortgageBalance

        // Total return calculation
        const totalInvestment = inputs.property.downPayment + inputs.property.closingCosts + inputs.property.renovationCosts
        const totalReturn = totalEquity - totalInvestment + cumulativeCashFlow + (taxBenefits * year)
        const annualizedReturn = Math.pow(totalReturn / totalInvestment + 1, 1 / year) - 1

        projections.push({
          year,
          propertyValue: currentPropertyValue,
          totalEquity,
          annualRent,
          totalExpenses,
          netCashFlow,
          cumulativeCashFlow,
          totalReturn,
          annualizedReturn: annualizedReturn * 100,
          mortgageBalance,
          principalPaydown: yearlyPrincipal,
          taxBenefits
        })
      }

      return projections
    }

    const moderateProjections = calculateScenario(scenarios.moderate.appreciation, scenarios.moderate.rentGrowth)
    const conservativeProjections = calculateScenario(scenarios.conservative.appreciation, scenarios.conservative.rentGrowth)
    const optimisticProjections = calculateScenario(scenarios.optimistic.appreciation, scenarios.optimistic.rentGrowth)

    // Calculate summary metrics
    const totalInvestment = inputs.property.downPayment + inputs.property.closingCosts + inputs.property.renovationCosts
    const finalProjection = moderateProjections[moderateProjections.length - 1]

    const totalCashFlow = finalProjection.cumulativeCashFlow
    const totalAppreciation = finalProjection.propertyValue - inputs.property.purchasePrice
    const totalTaxBenefits = finalProjection.taxBenefits * inputs.assumptions.holdingPeriod
    const totalReturn = finalProjection.totalReturn
    const annualizedReturn = finalProjection.annualizedReturn

    const firstYearCashFlow = moderateProjections[0].netCashFlow
    const cashOnCashReturn = (firstYearCashFlow / totalInvestment) * 100

    const firstYearNOI = moderateProjections[0].annualRent - (inputs.expenses.propertyTaxes + inputs.expenses.insurance + 
                         inputs.expenses.maintenance + inputs.expenses.repairs + inputs.expenses.utilities + inputs.expenses.other)
    const capRate = (firstYearNOI / inputs.property.purchasePrice) * 100

    const breakEvenYear = moderateProjections.findIndex(p => p.cumulativeCashFlow > 0) + 1

    const summary: InvestmentSummary = {
      totalInvestment,
      totalCashFlow,
      totalAppreciation,
      totalTaxBenefits,
      totalReturn,
      annualizedReturn,
      cashOnCashReturn,
      capRate,
      breakEvenYear: breakEvenYear || inputs.assumptions.holdingPeriod,
      projections: moderateProjections,
      scenarios: {
        conservative: conservativeProjections,
        moderate: moderateProjections,
        optimistic: optimisticProjections
      }
    }

    setResults(summary)
  }

  const calculateMonthlyPayment = (principal: number, rate: number, years: number) => {
    const monthlyRate = rate / 100 / 12
    const numPayments = years * 12
    return principal * (monthlyRate * Math.pow(1 + monthlyRate, numPayments)) / (Math.pow(1 + monthlyRate, numPayments) - 1)
  }

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount)
  }

  const formatPercent = (value: number) => {
    return `${value.toFixed(1)}%`
  }

  const getReturnColor = (value: number) => {
    if (value >= 15) return 'text-green-600'
    if (value >= 10) return 'text-blue-600'
    if (value >= 5) return 'text-yellow-600'
    return 'text-red-600'
  }

  const COLORS = ['#10b981', '#3b82f6', '#f59e0b', '#ef4444', '#8b5cf6']

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-green-100 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="text-center mb-12">
          <div className="flex justify-center items-center mb-4">
            <TrendingUp className="h-12 w-12 text-blue-600 mr-4" />
            <h1 className="text-4xl font-bold text-gray-900">Investment Growth Simulator</h1>
          </div>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            Analyze and visualize real estate investment performance with comprehensive projections, scenarios, and detailed financial analysis over time.
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Input Form */}
          <div className="lg:col-span-1">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Calculator className="h-5 w-5 mr-2" />
                  Investment Parameters
                </CardTitle>
                <CardDescription>
                  Enter your investment details for comprehensive analysis
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-6">
                <Tabs defaultValue="property" className="w-full">
                  <TabsList className="grid w-full grid-cols-5">
                    <TabsTrigger value="property">Property</TabsTrigger>
                    <TabsTrigger value="financing">Financing</TabsTrigger>
                    <TabsTrigger value="rental">Rental</TabsTrigger>
                    <TabsTrigger value="assumptions">Assumptions</TabsTrigger>
                    <TabsTrigger value="article"><BookOpen className="w-4 h-4 mr-2" />Article</TabsTrigger>
                  </TabsList>

                  <TabsContent value="property" className="space-y-4">
                    <div>
                      <Label htmlFor="purchasePrice">Purchase Price</Label>
                      <Input
                        id="purchasePrice"
                        type="number"
                        value={inputs.property.purchasePrice}
                        onChange={(e) => setInputs(prev => ({
                          ...prev,
                          property: { ...prev.property, purchasePrice: Number(e.target.value) }
                        }))}
                        placeholder="300000"
                      />
                    </div>

                    <div>
                      <Label htmlFor="downPayment">Down Payment</Label>
                      <Input
                        id="downPayment"
                        type="number"
                        value={inputs.property.downPayment}
                        onChange={(e) => {
                          const downPayment = Number(e.target.value)
