import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { ClosingCostBreakdown } from '../hooks/useClosingCostsCalculator';

interface ResultsProps {
  results: ClosingCostBreakdown | null;
}

const formatCurrency = (value: number) => {
  return new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD' }).format(value);
};

export const Results: React.FC<ResultsProps> = ({ results }) => {
  if (!results) return null;

  return (
    <Card className="mt-4">
      <CardHeader>
        <CardTitle>Closing Cost Breakdown</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-bold">Total Closing Costs</h3>
          <Badge variant="secondary" className="text-lg">{formatCurrency(results.totalClosingCosts)}</Badge>
        </div>
        <p className="text-sm text-gray-500 mb-4">
          Percentage of Purchase Price: {results.percentageOfPurchasePrice.toFixed(2)}%
        </p>
        <Separator />
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mt-4">
          <div>
            <h4 className="font-bold mb-2">Buyer Costs</h4>
            <ul>
              {Object.entries(results.buyerCosts).map(([key, value]) => (
                <li key={key} className="flex justify-between">
                  <span>{key.replace(/([A-Z])/g, ' $1').replace(/^./, (str) => str.toUpperCase())}</span>
                  <span>{formatCurrency(value as number)}</span>
                </li>
              ))}
            </ul>
          </div>
          <div>
            <h4 className="font-bold mb-2">Seller Costs</h4>
            <ul>
              {Object.entries(results.sellerCosts).map(([key, value]) => (
                <li key={key} className="flex justify-between">
                  <span>{key.replace(/([A-Z])/g, ' $1').replace(/^./, (str) => str.toUpperCase())}</span>
                  <span>{formatCurrency(value as number)}</span>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </CardContent>
    </Card>
  );
};

src/components/ui/form.tsx.content:
import * as React from "react"
import {
  Controller,
  ControllerProps,
  FieldPath,
  FieldValues,
  FormProvider,
  useFormContext,
} from "react-hook-form"

import { cn } from "@/lib/utils"
import { Label } from "@/components/ui/label"

const Form = FormProvider

type FormFieldContextValue<
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>
> = {
  name: TName
}

const FormFieldContext = React.createContext<FormFieldContextValue>(
  {} as FormFieldContextValue
)

const FormField = <
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>
>({
  ...props
}: ControllerProps<TFieldValues, TName>) => {
  return (
    <FormFieldContext.Provider value={{ name: props.name }}>
      <Controller {...props} />
    </FormFieldContext.Provider>
  )
}

const useFormField = () => {
  const fieldContext = React.useContext(FormFieldContext)
  const itemContext = React.useContext(FormItemContext)
  const { getFieldState, formState } = useFormContext()

  const fieldState = getFieldState(fieldContext.name, formState)

  if (!fieldContext) {
    throw new Error("useFormField should be used within <FormField>")
  }

  const { id } = itemContext

  return {
    id,
    name: fieldContext.name,
    formItemId: `${id}-form-item`,
    formDescriptionId: `${id}-form-item-description`,
    formMessageId: `${id}-form-item-message`,
    ...fieldState,
  }
}

type FormItemContextValue = {
  id: string
}

const FormItemContext = React.createContext<FormItemContextValue>(
  {} as FormItemContextValue
)

const FormItem = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const id = React.useId()

  return (
    <FormItemContext.Provider value={{ id }}>
      <div ref={ref} className={cn("space-y-2", className)} {...props} />
    </FormItemContext.Provider>
  )
})
FormItem.displayName = "FormItem"

const FormLabel = React.forwardRef<
  React.ElementRef<typeof Label>,
  React.ComponentPropsWithoutRef<typeof Label>
>(({ className, ...props }, ref) => {
  const { error, formItemId } = useFormField()

  return (
    <Label
      ref={ref}
      className={cn(error && "text-destructive", className)}
      htmlFor={formItemId}
      {...props}
    />
  )
})
FormLabel.displayName = "FormLabel"

const FormControl = React.forwardRef<
  React.ElementRef<typeof Slot>,
  React.ComponentPropsWithoutRef<typeof Slot>
>(({ ...props }, ref) => {
  const { error, formItemId, formDescriptionId, formMessageId } = useFormField()

  return (
    <Slot
      ref={ref}
      id={formItemId}
      aria-describedby={
        !error
          ? `${formDescriptionId}`
          : `${formDescriptionId} ${formMessageId}`
      }
      aria-invalid={!!error}
      {...props}
    />
  )
})
FormControl.displayName = "FormControl"

const FormDescription = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement>
>(({ className, ...props }, ref) => {
  const { formDescriptionId } = useFormField()

  return (
    <p
      ref={ref}
      id={formDescriptionId}
      className={cn("text-sm text-muted-foreground", className)}
      {...props}
    />
  )
})
FormDescription.displayName = "FormDescription"

const FormMessage = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement>
>(({ className, children, ...props }, ref) => {
  const { error, formMessageId } = useFormField()
  const body = error ? String(error?.message) : children

  if (!body) {
    return null
  }

  return (
    <p
      ref={ref}
      id={formMessageId}
      className={cn("text-sm font-medium text-destructive", className)}
      {...props}
    >
      {body}
    </p>
  )
})
FormMessage.displayName = "FormMessage"

// This is a custom component that we need to add to our project.
// It's a wrapper around the children that will be passed to it.
// It's used to get the props of the children and pass them to the parent.
// This is a common pattern in React to avoid prop drilling.
// We will use it in the FormControl component.
const Slot = React.forwardRef<
  HTMLElement,
  React.HTMLAttributes<HTMLElement>
>(({ children, ...props }, ref) => {
  const child = React.Children.only(children) as React.ReactElement
  return React.cloneElement(child, { ...props, ref })
})

Slot.displayName = "Slot"


export {
  useFormField,
  Form,
  FormItem,
  FormLabel,
  FormControl,
  FormDescription,
  FormMessage,
  FormField,
}

src/content/tools/amortization-calculato.content:
import { CalculatorContent } from "@/types";

export const amortizationCalculator: CalculatorContent = {
  title: "Amortization Calculator",
  description:
    "Calculate your amortization schedule to see how your loan is paid off over time.",
  icon: "Icon",
  category: "Calculators",
  slug: "amortization-calculator",
  article: {
    title: "Understanding Your Amortization Schedule",
    content:
      "An amortization schedule is a table detailing each periodic payment on a loan. It shows how much of each payment goes towards interest and how much goes towards paying off the principal. Use this calculator to see how your loan is paid off over time.",
  },
  calculator: {
    fields: [
        {
            name: "loanAmount",
            label: "Loan Amount",
            type: "number",
            defaultValue: "100000",
        },
        {
            name: "interestRate",
            label: "Interest Rate (%)",
            type: "number",
            defaultValue: "5",
        },
        {
            name: "loanTerm",
            label: "Loan Term (Years)",
            type: "number",
            defaultValue: "30",
        },
    ],
    results: [
        {
          label: "Monthly Payment",
          isCurrency: true,
        },
        {
          label: "Total Interest Paid",
          isCurrency: true,
        },
        {
          label: "Amortization Schedule",
          isCurrency: false,
        },
      ],
    calculate: (values) => {
      const { loanAmount, interestRate, loanTerm } = values;
      const principal = parseFloat(loanAmount);
      const interestRatePercent = parseFloat(interestRate);
      const loanTermYears = parseInt(loanTerm);

      const monthlyInterestRate = interestRatePercent / 100 / 12;
      const numberOfPayments = loanTermYears * 12;
      const monthlyPayment =
        (principal *
          monthlyInterestRate *
          Math.pow(1 + monthlyInterestRate, numberOfPayments)) /
        (Math.pow(1 + monthlyInterestRate, numberOfPayments) - 1);

      let remainingBalance = principal;
      const amortizationSchedule = [];

      for (let i = 1; i <= numberOfPayments; i++) {
        const interestPayment = remainingBalance * monthlyInterestRate;
        const principalPayment = monthlyPayment - interestPayment;
        remainingBalance -= principalPayment;
        amortizationSchedule.push({
          month: i,
          principal: principalPayment.toFixed(2),
          interest: interestPayment.toFixed(2),
          balance: remainingBalance.toFixed(2),
        });
      }

      return [
        {
          label: "Monthly Payment",
          value: monthlyPayment.toFixed(2),
          isCurrency: true,
        },
        {
          label: "Total Interest Paid",
          value: (monthlyPayment * numberOfPayments - principal).toFixed(2),
          isCurrency: true,
        },
        {
          label: "Amortization Schedule",
          value: amortizationSchedule,
          isCurrency: false,
        },
      ];
    },
  },
};

