import { CalculatorContent } from '@/types';

export const CAPITAL_GAIN_TAX_CALCULATOR_CONTENT: CalculatorContent = {
  title: 'Capital Gains Tax Calculator',
  description: 'Estimate the capital gains tax you may owe on the sale of a property. For informational purposes only.',
  icon: 'Icon',
  category: 'Taxes',
  slug: 'capital-gain-tax-calculator',
  article: {
    title: "Understanding Capital Gains Tax",
    content: `
    <h2>What is Capital Gains Tax?</h2>
    <h2>Understanding Capital Gain Tax on Real Estate</h2>
    <p>Capital gain tax is a tax on the profit from the sale of an asset, like real estate. It's the difference between the selling price and the purchase price, with adjustments for costs like improvements and selling expenses.</p>
    <h3>Key Factors</h3>
    <ul>
      <li><strong>Purchase Price:</strong> The original cost of the property.</li>
      <li><strong>Selling Price:</strong> The price you sell the property for.</li>
      <li><strong>Improvements:</strong> The cost of any capital improvements made to the property.</li>
      <li><strong>Selling Costs:</strong> Expenses like agent commissions and legal fees.</li>
      <li><strong>Holding Period:</strong> Whether it's a short-term (less than a year) or long-term (a year or more) gain determines the tax rate.</li>
    </ul>
  `,
},
  calculator: {
    results: [
      { label: 'Capital Gain', isCurrency: true },
      { label: 'Applicable Tax Rate (%)', isCurrency: false },
      { label: 'Estimated Tax', isCurrency: true },
    ],
    fields: [
      {
        name: 'purchasePrice',
        label: 'Purchase Price',
        type: 'number',
        defaultValue: 250000,
      },
      {
        name: 'sellingPrice',
        label: 'Selling Price',
        type: 'number',
        defaultValue: 400000,
      },
      {
        name: 'improvements',
        label: 'Cost of Improvements',
        type: 'number',
        defaultValue: 20000,
      },
      {
        name: 'sellingCosts',
        label: 'Selling Costs',
        type: 'number',
        defaultValue: 25000,
      },
      {
        name: 'isShortTerm',
        label: 'Short-Term Gain? (Held < 1 year)',
        type: 'boolean',
        defaultValue: false,
      },
      {
        name: 'income',
        label: 'Annual Income',
        type: 'number',
        defaultValue: 75000,
      },
    ],
    calculate: (data) => {
      const { purchasePrice, sellingPrice, improvements, sellingCosts, isShortTerm, income } = data;
      const capitalGain = sellingPrice - purchasePrice - improvements - sellingCosts;

      let taxRate = 0;
      if (isShortTerm) {
        if (income <= 40125) taxRate = 0.12;
        else if (income <= 85525) taxRate = 0.22;
        else if (income <= 163300) taxRate = 0.24;
        else if (income <= 207350) taxRate = 0.32;
        else if (income <= 518400) taxRate = 0.35;
        else taxRate = 0.37;
      } else {
        if (income <= 40400) taxRate = 0;
        else if (income <= 445850) taxRate = 0.15;
        else taxRate = 0.2;
      }

      const taxAmount = capitalGain * taxRate;

      return [
        { label: 'Capital Gain', value: capitalGain, isCurrency: true },
        { label: 'Applicable Tax Rate (%)', value: taxRate * 100, isCurrency: false },
        { label: 'Estimated Tax', value: taxAmount, isCurrency: true },
      ];
    },
  },
};

src/hooks/rental-property-calculator/use.content:
"use client";

import { create } from "zustand";

export interface RentalPropertyState {
  // Income
  monthlyRent: number;
  otherMonthlyIncome: number;

  // Expenses
  propertyTaxes: number;
  insurance: number;
  propertyManagementFees: number;
  maintenanceCosts: number;
  otherMonthlyExpenses: number;

  // Purchase Details
  purchasePrice: number;
  downPayment: number;
  interestRate: number;
  loanTerm: number;

  // Assumptions
  vacancyRate: number;
  appreciationRate: number;

  // Calculations
  totalMonthlyIncome: number;
  totalMonthlyExpenses: number;
  netOperatingIncome: number;
  monthlyMortgagePayment: number;
  cashFlow: number;
  capRate: number;
  cashOnCashReturn: number;

  setField: (field: keyof RentalPropertyState, value: number) => void;
  calculate: () => void;
}

export const useRentalPropertyCalculator = create<RentalPropertyState>((set, get) => ({
  // Income
  monthlyRent: 2000,
  otherMonthlyIncome: 0,

  // Expenses
  propertyTaxes: 200,
  insurance: 100,
  propertyManagementFees: 200,
  maintenanceCosts: 150,
  otherMonthlyExpenses: 50,

  // Purchase Details
  purchasePrice: 300000,
  downPayment: 60000,
  interestRate: 6,
  loanTerm: 30,

  // Assumptions
  vacancyRate: 5,
  appreciationRate: 3,

  // Calculations
  totalMonthlyIncome: 0,
  totalMonthlyExpenses: 0,
  netOperatingIncome: 0,
  monthlyMortgagePayment: 0,
  cashFlow: 0,
  capRate: 0,
  cashOnCashReturn: 0,

  setField: (field, value) => set({ [field]: value }),

  calculate: () => {
    const {
      monthlyRent,
      otherMonthlyIncome,
      propertyTaxes,
      insurance,
      propertyManagementFees,
      maintenanceCosts,
      otherMonthlyExpenses,
      purchasePrice,
      downPayment,
      interestRate,
      loanTerm,
      vacancyRate,
    } = get();

    const totalMonthlyIncome = monthlyRent + otherMonthlyIncome;
    const vacancyLoss = totalMonthlyIncome * (vacancyRate / 100);
    const effectiveGrossIncome = totalMonthlyIncome - vacancyLoss;

    const totalMonthlyExpenses =
      propertyTaxes +
      insurance +
      propertyManagementFees +
      maintenanceCosts +
      otherMonthlyExpenses;

    const netOperatingIncome = (effectiveGrossIncome - totalMonthlyExpenses) * 12;

    const loanAmount = purchasePrice - downPayment;
    const monthlyInterestRate = interestRate / 100 / 12;
    const numberOfPayments = loanTerm * 12;
    const monthlyMortgagePayment =
      loanAmount > 0
        ? (loanAmount *
            monthlyInterestRate *
            Math.pow(1 + monthlyInterestRate, numberOfPayments)) /
          (Math.pow(1 + monthlyInterestRate, numberOfPayments) - 1)
        : 0;

    const cashFlow = effectiveGrossIncome - totalMonthlyExpenses - monthlyMortgagePayment;
    const capRate = (netOperatingIncome / purchasePrice) * 100;
    const totalInvestment = downPayment;
    const cashOnCashReturn = ((cashFlow * 12) / totalInvestment) * 100;

    set({
      totalMonthlyIncome,
      totalMonthlyExpenses,
      netOperatingIncome,
      monthlyMortgagePayment,
      cashFlow,
      capRate,
      cashOnCashReturn,
    });
  },
}));

src/content/tools/cost-of-living-calcula.content:
import { CalculatorContent } from '@/types';

export const COST_OF_LIVING_CALCULATOR_CONTENT: CalculatorContent = {
  title: 'Cost of Living Calculator',
  description: 'Compare the cost of living between two cities. See how your salary and expenses may change.',
  icon: 'Icon',
  slug: 'cost-of-living-calculator',
  category: 'General',
  article: {
    title: 'Comparing the Cost of Living',
    content: `
    <h2>Comparing the Cost of Living</h2>
    <p>Whether you are moving for a new job, retirement, or a change of scenery, it is crucial to understand how your cost of living will change. The cost of living can vary significantly from one city to another, and a higher salary in a new city does not always mean you will have more disposable income. This calculator will help you compare the cost of living between two cities.</p>

    <h3>What is the Cost of Living?</h3>
    <p>The cost of living is the amount of money needed to cover basic expenses such as housing, food, taxes, and healthcare in a particular place. It is often used to compare how expensive it is to live in one city versus another.</p>

    <h3>Key Factors in the Cost of Living</h3>
    <p>Several factors contribute to the cost of living in a city:</p>
    <ul>
      <li><strong>Housing:</strong> This is usually the biggest expense, and it includes rent or mortgage payments, property taxes, and utilities.</li>
      <li><strong>Food:</strong> The cost of groceries and dining out.</li>
      <li><strong>Transportation:</strong> The cost of gas, public transportation, car insurance, and maintenance.</li>
      <li><strong>Healthcare:</strong> The cost of health insurance premiums, doctor visits, and prescription drugs.</li>
      <li><strong>Taxes:</strong> State and local income taxes, sales taxes, and property taxes.</li>
      <li><strong>Other Expenses:</strong> The cost of entertainment, clothing, and personal care.</li>
    </ul>

    <h3>How to Use the Cost of Living Calculator</h3>
    <p>This calculator uses a simplified model and does not use a real-time cost of living index. Instead, it allows you to input your current salary and the major monthly expenses in your current city and the new city you are considering. You will need to provide estimates for:</p>
    <ul>
      <li>Your current annual salary.</li>
      <li>Monthly housing costs (rent/mortgage).</li>
      <li>Monthly transportation costs.</li>
      <li>Monthly food costs.</li>
      <li>Other monthly expenses.</li>
    </ul>
    <p>The calculator will then compare your total monthly expenses in both cities and estimate the equivalent salary you would need in the new city to maintain your current lifestyle. This will give you a better idea of how a move could impact your finances.</p>
  `,
  },
  calculator: {
    results: [],
    fields: [
      {
        name: 'currentSalary',
        label: 'Current Annual Salary',
        type: 'number',
        defaultValue: 60000,
      },
      {
        name: 'currentHousing',
        label: 'Current Monthly Housing Cost',
        type: 'number',
        defaultValue: 1200,
      },
      {
        name: 'newHousing',
        label: 'New City Monthly Housing Cost',
        type: 'number',
        defaultValue: 1800,
      },
      {
        name: 'currentFood',
        label: 'Current Monthly Food Cost',
        type: 'number',
        defaultValue: 400,
      },
      {
        name: 'newFood',
        label: 'New City Monthly Food Cost',
        type: 'number',
        defaultValue: 500,
      },
      {
        name: 'currentTransportation',
        label: 'Current Monthly Transportation Cost',
        type: 'number',
        defaultValue: 300,
      },
      {
        name: 'newTransportation',
        label: 'New City Monthly Transportation Cost',
        type: 'number',
        defaultValue: 400,
      },
    ],
    calculate: (values) => {
      const { currentSalary, currentHousing, newHousing, currentFood, newFood, currentTransportation, newTransportation } = values;
      const currentTotalMonthlyCosts = currentHousing + currentFood + currentTransportation;
      const newTotalMonthlyCosts = newHousing + newFood + newTransportation;
      const costDifference = newTotalMonthlyCosts - currentTotalMonthlyCosts;
      const equivalentSalary = currentSalary + (costDifference * 12);

      return [
        { label: 'Equivalent Salary in New City', value: equivalentSalary.toFixed(2), isCurrency: true },
        { label: 'Difference in Monthly Costs', value: costDifference.toFixed(2), isCurrency: true },
      ];
    },
  },
};

