src/components/calculator-enhancements/C.content:
'use client';

import React from 'react';
import { useCalculatorEnhancements, SavedScenario } from '@/store/calculatorEnhancementsStore';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { ChartBarIcon, XMarkIcon } from '@heroicons/react/24/outline';

interface ComparisonMatrixProps {
  calculatorSlug: string;
  onClose?: () => void;
}

export const ComparisonMatrix: React.FC<ComparisonMatrixProps> = ({
  calculatorSlug,
  onClose,
}) => {
  const { getScenariosByCalculator } = useCalculatorEnhancements();
  const scenarios = getScenariosByCalculator(calculatorSlug).slice(0, 5);

  if (scenarios.length < 2) {
    return (
      <Card className="border-orange-200">
        <CardHeader>
          <CardTitle className="text-lg flex items-center gap-2">
            <ChartBarIcon className="w-5 h-5" />
            Scenario Comparison
          </CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-sm text-gray-600">
            Save at least 2 scenarios to compare them side-by-side.
          </p>
        </CardContent>
      </Card>
    );
  }

  // Get all unique result labels
  const allResultLabels = Array.from(
    new Set(scenarios.flatMap(s => s.results.map(r => r.label)))
  );

  // Helper to get result value for a specific scenario and label
  const getResultValue = (scenario: SavedScenario, label: string): string => {
    const result = scenario.results.find(r => r.label === label);
    return result?.value || '-';
  };

  // Helper to parse numeric value from formatted string
  const parseNumericValue = (value: string): number | null => {
    const cleaned = String(value).replace(/[^0-9.-]/g, '');
    const parsed = parseFloat(cleaned);
    return isNaN(parsed) ? null : parsed;
  };

  // Find best (highest) and worst (lowest) for each metric
  const getBestAndWorst = (label: string) => {
    const values = scenarios.map(s => {
      const val = getResultValue(s, label);
      return parseNumericValue(val);
    });

    const validValues = values.filter(v => v !== null) as number[];
    if (validValues.length === 0) return { best: null, worst: null };

    const max = Math.max(...validValues);
    const min = Math.min(...validValues);

    return { best: max, worst: min };
  };

  const getCellStyle = (scenario: SavedScenario, label: string): string => {
    const value = getResultValue(scenario, label);
    const numValue = parseNumericValue(value);
    if (numValue === null) return '';

    const { best, worst } = getBestAndWorst(label);
    if (best === null || worst === null) return '';

    if (numValue === best) {
      return 'bg-green-100 border-green-300 font-semibold';
    } else if (numValue === worst) {
      return 'bg-red-100 border-red-300';
    }
    return '';
  };

  return (
    <Card className="border-orange-200">
      <CardHeader className="flex flex-row items-center justify-between">
        <CardTitle className="text-lg flex items-center gap-2">
          <ChartBarIcon className="w-5 h-5" />
          Scenario Comparison Matrix
        </CardTitle>
        {onClose && (
          <Button variant="ghost" size="sm" onClick={onClose}>
            <XMarkIcon className="w-5 h-5" />
          </Button>
        )}
      </CardHeader>
      <CardContent>
        <div className="overflow-x-auto">
          <table className="w-full border-collapse text-sm">
            <thead>
              <tr className="bg-gray-100">
                <th className="border border-gray-300 p-2 text-left font-semibold">
                  Metric
                </th>
                {scenarios.map((scenario) => (
                  <th
                    key={scenario.id}
                    className="border border-gray-300 p-2 text-left font-semibold min-w-[150px]"
                  >
                    <div className="flex flex-col">
                      <span>{scenario.name}</span>
                      <span className="text-xs text-gray-500 font-normal">
                        {new Date(scenario.timestamp).toLocaleDateString()}
                      </span>
                    </div>
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              {allResultLabels.map((label) => (
                <tr key={label} className="hover:bg-gray-50">
                  <td className="border border-gray-300 p-2 font-medium">
                    {label}
                  </td>
                  {scenarios.map((scenario) => (
                    <td
                      key={scenario.id}
                      className={`border border-gray-300 p-2 ${getCellStyle(
                        scenario,
                        label
                      )}`}
                    >
                      {getResultValue(scenario, label)}
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div className="mt-4 flex items-center gap-4 text-xs text-gray-600">
          <div className="flex items-center gap-2">
            <div className="w-4 h-4 bg-green-100 border border-green-300 rounded" />
            <span>Best Value</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-4 h-4 bg-red-100 border border-red-300 rounded" />
            <span>Worst Value</span>
          </div>
        </div>
      </CardContent>
    </Card>
  );
};

src/lib/smartInsights.ts.content:
export interface Insight {
  type: 'success' | 'warning' | 'info' | 'danger';
  title: string;
  message: string;
  recommendation?: string;
}

/**
 * Generate smart insights based on calculation results
 */
export const generateInsights = (
  calculatorType: string,
  inputs: Record<string, any>,
  results: any[]
): Insight[] => {
  const insights: Insight[] = [];

  // Mortgage Calculator Insights
  if (calculatorType.includes('mortgage') || calculatorType.includes('loan')) {
    const loanAmount = parseFloat(inputs.loanAmount || inputs.propertyPrice || 0);
    const downPayment = parseFloat(inputs.downPayment || 0);
    const interestRate = parseFloat(inputs.interestRate || inputs.rate || 0);
    const term = parseFloat(inputs.term || inputs.loanTerm || 0);

    // Down payment analysis
    const downPaymentPercent = (downPayment / loanAmount) * 100;
    if (downPaymentPercent < 20) {
      insights.push({
        type: 'warning',
        title: 'Low Down Payment',
        message: `Your down payment is ${downPaymentPercent.toFixed(1)}% of the loan amount.`,
        recommendation: 'Consider saving for a 20% down payment to avoid PMI (Private Mortgage Insurance) and reduce your monthly payments.'
      });
    } else if (downPaymentPercent >= 20) {
      insights.push({
        type: 'success',
        title: 'Excellent Down Payment',
        message: `Your ${downPaymentPercent.toFixed(1)}% down payment will help you avoid PMI and secure better interest rates.`,
      });
    }

    // Interest rate analysis
    if (interestRate > 7) {
      insights.push({
        type: 'warning',
        title: 'High Interest Rate',
        message: `Your interest rate of ${interestRate}% is relatively high.`,
        recommendation: 'Consider shopping around for better rates or improving your credit score to qualify for lower rates.'
      });
    } else if (interestRate < 4) {
      insights.push({
        type: 'success',
        title: 'Excellent Interest Rate',
        message: `Your ${interestRate}% interest rate is very competitive!`,
      });
    }

    // Loan term analysis
    if (term > 25) {
      insights.push({
        type: 'info',
        title: 'Long Loan Term',
        message: `Your ${term}-year loan term will result in lower monthly payments but higher total interest.`,
        recommendation: 'Consider a shorter term if you can afford higher monthly payments to save significantly on interest.'
      });
    }
  }

  // Investment Calculator Insights
  if (calculatorType.includes('roi') || calculatorType.includes('investment') || calculatorType.includes('rental')) {
    const roi = results.find(r => r.label.toLowerCase().includes('roi'))?.value;
    const cashFlow = results.find(r => r.label.toLowerCase().includes('cash flow'))?.value;
    const capRate = results.find(r => r.label.toLowerCase().includes('cap rate'))?.value;

    // ROI Analysis
    if (roi) {
      const roiValue = parseFloat(String(roi).replace(/[^0-9.-]/g, ''));
      if (roiValue < 0) {
        insights.push({
          type: 'danger',
          title: 'Negative ROI',
          message: 'This investment is projected to lose money.',
          recommendation: 'Reconsider this investment or negotiate a better purchase price.'
        });
      } else if (roiValue < 5) {
        insights.push({
          type: 'warning',
          title: 'Low ROI',
          message: `ROI of ${roiValue.toFixed(1)}% is below average for real estate investments.`,
          recommendation: 'Look for opportunities to increase rental income or reduce expenses.'
        });
      } else if (roiValue >= 10) {
        insights.push({
          type: 'success',
          title: 'Excellent ROI',
          message: `ROI of ${roiValue.toFixed(1)}% is outstanding for real estate!`,
        });
      }
    }

    // Cash Flow Analysis
    if (cashFlow) {
      const cashFlowValue = parseFloat(String(cashFlow).replace(/[^0-9.-]/g, ''));
      if (cashFlowValue < 0) {
        insights.push({
          type: 'danger',
          title: 'Negative Cash Flow',
          message: 'This property will cost you money each month.',
          recommendation: 'Increase rental income or reduce operating expenses to achieve positive cash flow.'
        });
      } else if (cashFlowValue > 0 && cashFlowValue < 100) {
        insights.push({
          type: 'warning',
          title: 'Minimal Cash Flow',
          message: 'Cash flow is positive but very thin.',
          recommendation: 'Build a larger cash reserve to handle unexpected expenses.'
        });
      } else if (cashFlowValue >= 200) {
        insights.push({
          type: 'success',
          title: 'Strong Cash Flow',
          message: `Monthly cash flow of $${cashFlowValue.toFixed(0)} provides excellent cushion.`,
        });
      }
    }

    // Cap Rate Analysis
    if (capRate) {
      const capRateValue = parseFloat(String(capRate).replace(/[^0-9.-]/g, ''));
      if (capRateValue < 4) {
        insights.push({
          type: 'info',
          title: 'Low Cap Rate',
          message: `Cap rate of ${capRateValue.toFixed(1)}% suggests this is in a high-growth area.`,
          recommendation: 'Lower cap rates often mean higher property appreciation potential.'
        });
      } else if (capRateValue > 10) {
        insights.push({
          type: 'warning',
          title: 'High Cap Rate',
          message: `Cap rate of ${capRateValue.toFixed(1)}% may indicate higher risk or needed renovations.`,
          recommendation: 'Verify property condition and neighborhood quality before investing.'
        });
      }
    }
  }

  // Debt Service Coverage Ratio
  if (calculatorType.includes('debt') || calculatorType.includes('dscr')) {
    const dscr = results.find(r => r.label.toLowerCase().includes('dscr') || r.label.toLowerCase().includes('coverage'))?.value;
    if (dscr) {
      const dscrValue = parseFloat(String(dscr).replace(/[^0-9.-]/g, ''));
      if (dscrValue < 1) {
        insights.push({
          type: 'danger',
          title: 'DSCR Below 1',
          message: 'Income does not cover debt payments.',
          recommendation: 'Most lenders require DSCR of at least 1.25. Increase income or reduce debt.'
        });
      } else if (dscrValue >= 1 && dscrValue < 1.25) {
        insights.push({
          type: 'warning',
          title: 'Low DSCR',
          message: `DSCR of ${dscrValue.toFixed(2)} may not meet lender requirements.`,
          recommendation: 'Aim for DSCR of 1.25 or higher for better financing options.'
        });
      } else if (dscrValue >= 1.25) {
        insights.push({
          type: 'success',
          title: 'Strong DSCR',
          message: `DSCR of ${dscrValue.toFixed(2)} meets or exceeds lender requirements.`,
        });
      }
    }
  }

  // General insights
  if (insights.length === 0) {
    insights.push({
      type: 'info',
      title: 'Calculation Complete',
      message: 'Review your results carefully and consider consulting with a financial advisor for personalized advice.',
    });
  }

  return insights;
};

/**
 * Generate What-If scenarios for sensitivity analysis
 */
export const generateWhatIfScenarios = (
  baseInputs: Record<string, any>,
  variableKey: string,
  changePercents: number[] = [-20, -10, 0, 10, 20]
): Record<string, any>[] => {
  const baseValue = parseFloat(baseInputs[variableKey] || 0);
  return changePercents.map(percent => ({
    ...baseInputs,
    [variableKey]: baseValue * (1 + percent / 100),
    _scenarioLabel: `${percent >= 0 ? '+' : ''}${percent}%`,
    _changePercent: percent,
  }));
};

src/store/calculatorEnhancementsStore.ts.content:
import { create } from 'zustand';
import { persist } from 'zustand/middleware';

export interface SavedScenario {
  id: string;
  calculatorSlug: string;
  calculatorTitle: string;
  name: string;
  inputs: Record<string, any>;
  results: any[];
  timestamp: number;
  notes?: string;
  tags?: string[];
  isFavorite?: boolean;
}

export interface CalculationHistory {
  id: string;
  calculatorSlug: string;
  calculatorTitle: string;
  inputs: Record<string, any>;
  results: any[];
  timestamp: number;
}

export interface FavoriteTool {
  slug: string;
  title: string;
  category: string;
  addedAt: number;
}

export interface Goal {
  id: string;
  title: string;
  targetValue: number;
  currentValue: number;
  deadline?: number;
  relatedCalculator?: string;
  createdAt: number;
  updatedAt: number;
  isCompleted: boolean;
  milestones?: { value: number; label: string; achieved: boolean }[];
}

interface EnhancementsStore {
  scenarios: SavedScenario[];
  history: CalculationHistory[];
  favorites: FavoriteTool[];
  goals: Goal[];
  activeScenario: string | null;

  // Scenario Methods
  saveScenario: (scenario: Omit<SavedScenario, 'id' | 'timestamp'>) => string;
  updateScenario: (id: string, updates: Partial<SavedScenario>) => void;
  deleteScenario: (id: string) => void;
  getScenariosByCalculator: (slug: string) => SavedScenario[];
  setActiveScenario: (id: string | null) => void;

  // History Methods
  addToHistory: (entry: Omit<CalculationHistory, 'id' | 'timestamp'>) => void;
  getHistoryByCalculator: (slug: string) => CalculationHistory[];
  clearHistory: () => void;
  deleteHistoryEntry: (id: string) => void;

  // Favorites Methods
  addFavorite: (tool: Omit<FavoriteTool, 'addedAt'>) => void;
  removeFavorite: (slug: string) => void;
  isFavorite: (slug: string) => boolean;

  // Goals Methods
  addGoal: (goal: Omit<Goal, 'id' | 'createdAt' | 'updatedAt' | 'isCompleted'>) => string;
  updateGoal: (id: string, updates: Partial<Goal>) => void;
  deleteGoal: (id: string) => void;
  markGoalComplete: (id: string) => void;
  updateGoalProgress: (id: string, currentValue: number) => void;
}

export const useCalculatorEnhancements = create<EnhancementsStore>()(
  persist(
    (set, get) => ({
      scenarios: [],
      history: [],
      favorites: [],
      goals: [],
      activeScenario: null,

      // Scenario Methods
      saveScenario: (scenario) => {
        const id = `scenario_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
        const newScenario: SavedScenario = {
          ...scenario,
          id,
          timestamp: Date.now(),
        };
        set((state) => ({ scenarios: [...state.scenarios, newScenario] }));
        return id;
      },

      updateScenario: (id, updates) => {
        set((state) => ({
          scenarios: state.scenarios.map((s) =>
            s.id === id ? { ...s, ...updates } : s
          ),
        }));
      },

      deleteScenario: (id) => {
        set((state) => ({
          scenarios: state.scenarios.filter((s) => s.id !== id),
          activeScenario: state.activeScenario === id ? null : state.activeScenario,
        }));
      },

      getScenariosByCalculator: (slug) => {
        return get().scenarios.filter((s) => s.calculatorSlug === slug);
      },

      setActiveScenario: (id) => {
        set({ activeScenario: id });
      },

      // History Methods
      addToHistory: (entry) => {
        const id = `history_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
        const newEntry: CalculationHistory = {
          ...entry,
          id,
          timestamp: Date.now(),
        };
        set((state) => {
          // Keep only last 50 entries per calculator
          const existingHistory = state.history.filter(h => h.calculatorSlug === entry.calculatorSlug);
          const otherHistory = state.history.filter(h => h.calculatorSlug !== entry.calculatorSlug);
          const limitedHistory = existingHistory.length >= 50 
            ? existingHistory.slice(-49) 
            : existingHistory;
          return { history: [...otherHistory, ...limitedHistory, newEntry] };
        });
      },

      getHistoryByCalculator: (slug) => {
        return get().history.filter((h) => h.calculatorSlug === slug)
          .sort((a, b) => b.timestamp - a.timestamp);
      },

      clearHistory: () => {
        set({ history: [] });
      },

      deleteHistoryEntry: (id) => {
        set((state) => ({
          history: state.history.filter((h) => h.id !== id),
        }));
      },

      // Favorites Methods
      addFavorite: (tool) => {
        const newFavorite: FavoriteTool = {
          ...tool,
          addedAt: Date.now(),
        };
        set((state) => ({ favorites: [...state.favorites, newFavorite] }));
      },

      removeFavorite: (slug) => {
        set((state) => ({
          favorites: state.favorites.filter((f) => f.slug !== slug),
        }));
      },

      isFavorite: (slug) => {
        return get().favorites.some((f) => f.slug === slug);
      },

      // Goals Methods
      addGoal: (goal) => {
        const id = `goal_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
        const newGoal: Goal = {
          ...goal,
          id,
          createdAt: Date.now(),
          updatedAt: Date.now(),
          isCompleted: false,
        };
        set((state) => ({ goals: [...state.goals, newGoal] }));
        return id;
      },

      updateGoal: (id, updates) => {
        set((state) => ({
          goals: state.goals.map((g) =>
            g.id === id ? { ...g, ...updates, updatedAt: Date.now() } : g
          ),
        }));
      },

      deleteGoal: (id) => {
        set((state) => ({
          goals: state.goals.filter((g) => g.id !== id),
        }));
      },

      markGoalComplete: (id) => {
        set((state) => ({
          goals: state.goals.map((g) =>
            g.id === id ? { ...g, isCompleted: true, updatedAt: Date.now() } : g
          ),
        }));
      },

      updateGoalProgress: (id, currentValue) => {
        set((state) => ({
          goals: state.goals.map((g) => {
            if (g.id === id) {
              const updated = { ...g, currentValue, updatedAt: Date.now() };
              // Check if goal is completed
              if (currentValue >= g.targetValue) {
                updated.isCompleted = true;
              }
              return updated;
            }
            return g;
          }),
        }));
      },
    }),
    {
      name: 'calculator-enhancements-storage',
    }
  )
);

