'use client';

import React, { useState } from 'react';
import { useCalculatorEnhancements } from '@/store/calculatorEnhancementsStore';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { ClockIcon, TrashIcon, ArrowPathIcon } from '@heroicons/react/24/outline';

interface CalculationHistoryProps {
  calculatorSlug: string;
  onLoadHistory: (inputs: Record<string, any>) => void;
}

export const CalculationHistory: React.FC<CalculationHistoryProps> = ({
  calculatorSlug,
  onLoadHistory,
}) => {
  const { getHistoryByCalculator, deleteHistoryEntry, clearHistory } = useCalculatorEnhancements();
  const [showAll, setShowAll] = useState(false);

  const history = getHistoryByCalculator(calculatorSlug);
  const displayHistory = showAll ? history : history.slice(0, 5);

  if (history.length === 0) {
    return null;
  }

  return (
    <Card className="border-gray-200">
      <CardHeader className="flex flex-row items-center justify-between">
        <CardTitle className="text-lg flex items-center gap-2">
          <ClockIcon className="w-5 h-5" />
          Calculation History ({history.length})
        </CardTitle>
        <Button
          variant="ghost"
          size="sm"
          onClick={() => {
            if (confirm('Are you sure you want to clear all history for this calculator?')) {
              clearHistory();
            }
          }}
          className="text-red-600 hover:text-red-700"
        >
          Clear All
        </Button>
      </CardHeader>
      <CardContent className="space-y-2">
        {displayHistory.map((entry) => (
          <div
            key={entry.id}
            className="border border-gray-200 rounded-lg p-3 hover:border-blue-300 transition-all"
          >
            <div className="flex items-start justify-between">
              <div className="flex-1">
                <p className="text-xs text-gray-500">
                  {new Date(entry.timestamp).toLocaleString()}
                </p>
                <div className="mt-1 text-xs text-gray-600">
                  {Object.entries(entry.inputs).slice(0, 3).map(([key, value]) => (
                    <div key={key} className="flex justify-between">
                      <span className="font-medium">{key}:</span>
                      <span>{String(value)}</span>
                    </div>
                  ))}
                  {Object.keys(entry.inputs).length > 3 && (
                    <p className="text-gray-400 text-xs mt-1">
                      +{Object.keys(entry.inputs).length - 3} more fields
                    </p>
                  )}
                </div>
              </div>
              <div className="flex gap-1 ml-2">
                <Button
                  size="sm"
                  variant="outline"
                  onClick={() => onLoadHistory(entry.inputs)}
                  title="Reload these values"
                >
                  <ArrowPathIcon className="w-4 h-4" />
                </Button>
                <Button
                  size="sm"
                  variant="ghost"
                  onClick={() => deleteHistoryEntry(entry.id)}
                  className="text-red-600 hover:text-red-700"
                  title="Delete this entry"
                >
                  <TrashIcon className="w-4 h-4" />
                </Button>
              </div>
            </div>
          </div>
        ))}

        {history.length > 5 && (
          <Button
            variant="outline"
            size="sm"
            onClick={() => setShowAll(!showAll)}
            className="w-full"
          >
            {showAll ? 'Show Less' : `Show All (${history.length})`}
          </Button>
        )}
      </CardContent>
    </Card>
  );
};

src/components/calculators/EnhancedCalcu.content:
'use client';

import React, { useEffect, useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { ICalculator } from '@/types/calculators';
import { useCalculator } from './hooks/useCalculator';
import { CalculatorFields } from './components/CalculatorFields';
import { CalculatorResults } from './components/CalculatorResults';
import { useCalculatorEnhancements } from '@/store/calculatorEnhancementsStore';
import { parseShareLink } from '@/lib/exportUtils';
import {
  ScenarioManager,
  ExportButtons,
  ShareButton,
  SmartInsights,
  CalculationHistory,
  NotesPanel,
  ComparisonMatrix,
  FavoriteButton,
  WhatIfAnalysis,
} from '../calculator-enhancements';

interface EnhancedCalculatorProps extends ICalculator {
  slug: string;
  category?: string;
}

export const EnhancedCalculator: React.FC<EnhancedCalculatorProps> = ({
  title,
  description,
  inputs,
  results,
  calculation,
  slug,
  category = 'Real Estate',
}) => {
  const { values, results: displayResults, handleChange, handleCalculate, setValues } = useCalculator(
    inputs,
    results,
    calculation
  );
  const { addToHistory } = useCalculatorEnhancements();
  const [notes, setNotes] = useState('');
  const [showComparison, setShowComparison] = useState(false);
  const [activeTab, setActiveTab] = useState('calculator');

  // Load shared calculation if present in URL (once on mount)
  useEffect(() => {
    try {
      const sharedData = parseShareLink();
      if (sharedData && sharedData.inputs && Object.keys(sharedData.inputs).length > 0) {
        setValues(sharedData.inputs);
      }
    } catch (error) {
      console.error('Failed to load shared calculation:', error);
    }
  }, []);

  // Auto-calculate when values change
  useEffect(() => {
    const calculateAndSave = () => {
      handleCalculate();
    };

    calculateAndSave();
  }, [values]);

  // Save to history when results are available (separate effect)
  useEffect(() => {
    if (!displayResults || displayResults.length === 0) return;

    // Debounce history saving
    const timeoutId = setTimeout(() => {
      addToHistory({
        calculatorSlug: slug,
        calculatorTitle: title,
        inputs: values,
        results: displayResults,
      });
    }, 1500);

    return () => clearTimeout(timeoutId);
  }, [displayResults, slug, title, values, addToHistory]);

  const handleLoadScenario = (scenarioInputs: Record<string, any>) => {
    setValues(scenarioInputs);
    setActiveTab('calculator');
  };

  return (
    <div className="space-y-6">
      {/* Header with Favorite Button */}
      <div className="flex items-center justify-between flex-wrap gap-4">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">{title}</h1>
          {description && (
            <p className="text-gray-600 mt-2">{description}</p>
          )}
        </div>
        <FavoriteButton slug={slug} title={title} category={category} />
      </div>

      <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="calculator">Calculator</TabsTrigger>
          <TabsTrigger value="scenarios">Scenarios</TabsTrigger>
          <TabsTrigger value="insights">Insights</TabsTrigger>
          <TabsTrigger value="comparison">Compare</TabsTrigger>
        </TabsList>

        <TabsContent value="calculator" className="space-y-6 mt-6">
          {/* Main Calculator Card */}
          <Card className="border-0 shadow-xl">
            <CardHeader className="bg-gradient-to-r from-blue-600 to-indigo-600 text-white rounded-t-xl pb-8">
              <CardTitle className="text-3xl text-white">Calculator Inputs</CardTitle>
            </CardHeader>
            <CardContent className="pt-8 pb-8 space-y-6">
              <CalculatorFields fields={inputs} values={values} handleChange={handleChange} />

              {/* What-If Analysis */}
              <WhatIfAnalysis inputs={inputs} values={values} onValueChange={handleChange} />

              <Button
                onClick={handleCalculate}
                className="mt-4 w-full md:w-auto bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white font-bold py-3 px-10 rounded-lg shadow-lg transition-all duration-300 hover:shadow-2xl hover:scale-105"
              >
                Calculate Now
              </Button>
            </CardContent>
          </Card>

          {/* Results Section */}
          {displayResults && displayResults.length > 0 && (
            <Card className="border-0 shadow-xl bg-gradient-to-br from-green-50 to-emerald-50">
              <CardHeader className="bg-gradient-to-r from-green-600 to-emerald-600 text-white rounded-t-xl pb-8">
                <div className="flex items-center justify-between flex-wrap gap-4">
                  <CardTitle className="text-3xl text-white">Your Results</CardTitle>
                  <div className="flex flex-wrap gap-2">
                    <ShareButton
                      calculatorTitle={title}
                      inputs={values}
                      results={displayResults}
                    />
                    <ExportButtons
                      calculatorTitle={title}
                      inputs={values}
                      results={displayResults}
                      notes={notes}
                    />
                  </div>
                </div>
              </CardHeader>
              <CardContent className="pt-8 pb-8">
                <CalculatorResults results={displayResults} />
              </CardContent>
            </Card>
          )}

          {/* Notes Panel */}
          <NotesPanel notes={notes} onNotesChange={setNotes} />

          {/* Calculation History */}
          <CalculationHistory
            calculatorSlug={slug}
            onLoadHistory={handleLoadScenario}
          />
        </TabsContent>

        <TabsContent value="scenarios" className="space-y-6 mt-6">
          <ScenarioManager
            calculatorSlug={slug}
            calculatorTitle={title}
            currentInputs={values}
            currentResults={displayResults || []}
            onLoadScenario={handleLoadScenario}
          />
        </TabsContent>

        <TabsContent value="insights" className="space-y-6 mt-6">
          {displayResults && displayResults.length > 0 ? (
            <SmartInsights
              calculatorType={slug}
              inputs={values}
              results={displayResults}
            />
          ) : (
            <Card>
              <CardContent className="py-12 text-center text-gray-500">
                Calculate first to see smart insights and recommendations.
              </CardContent>
            </Card>
          )}
        </TabsContent>

        <TabsContent value="comparison" className="space-y-6 mt-6">
          <ComparisonMatrix calculatorSlug={slug} />
        </TabsContent>
      </Tabs>
    </div>
  );
};

src/components/calculator-enhancements/R.content:
'use client';

import React from 'react';
import { AdvancedCharts } from './AdvancedCharts';

interface ResultsVisualizationProps {
  calculatorType: string;
  inputs: Record<string, any>;
  results: any[];
}

export const ResultsVisualization: React.FC<ResultsVisualizationProps> = ({
  calculatorType,
  inputs,
  results,
}) => {
  // For mortgage/loan calculators
  if (calculatorType.includes('mortgage') || calculatorType.includes('loan') || calculatorType.includes('amortization')) {
    const loanAmount = parseFloat(inputs.loanAmount || inputs.propertyPrice || 0);
    const interestRate = parseFloat(inputs.interestRate || inputs.rate || 0);
    const term = parseFloat(inputs.term || inputs.loanTerm || 0);
    const monthlyPayment = parseFloat(String(results.find(r => r.label.toLowerCase().includes('monthly'))?.value || '0').replace(/[^0-9.-]/g, ''));

    if (loanAmount && interestRate && term && monthlyPayment) {
      const data: any[] = [];
      let balance = loanAmount;
      const monthlyRate = interestRate / 100 / 12;
      const termMonths = term * 12;

      for (let year = 0; year <= term; year += Math.max(1, Math.floor(term / 10))) {
        const monthsPassed = year * 12;
        if (monthsPassed <= termMonths) {
          const monthlyInterest = balance * monthlyRate;
          const monthlyPrincipal = monthlyPayment - monthlyInterest;

          data.push({
            name: `Year ${year}`,
            Principal: Math.round(monthlyPrincipal * 12),
            Interest: Math.round(monthlyInterest * 12),
            Balance: Math.round(balance),
          });

          // Update balance for next year
          for (let m = 0; m < 12 && balance > 0; m++) {
            const interest = balance * monthlyRate;
            const principal = monthlyPayment - interest;
            balance = Math.max(0, balance - principal);
          }
        }
      }

      return (
        <div className="space-y-6">
          <AdvancedCharts
            data={data}
            type="area"
            title="Loan Balance Over Time"
            dataKeys={['Balance']}
            colors={['#3b82f6']}
          />
          <AdvancedCharts
            data={data}
            type="bar"
            title="Annual Principal vs Interest Payments"
            dataKeys={['Principal', 'Interest']}
            colors={['#10b981', '#ef4444']}
          />
        </div>
      );
    }
  }

  // For investment/ROI calculators
  if (calculatorType.includes('roi') || calculatorType.includes('investment') || calculatorType.includes('rental')) {
    const purchasePrice = parseFloat(inputs.purchasePrice || inputs.propertyValue || inputs.initialInvestment || 0);
    const appreciationRate = parseFloat(inputs.appreciationRate || inputs.growthRate || 3);

    if (purchasePrice) {
      const projectionData: any[] = [];
      for (let year = 0; year <= 10; year++) {
        const value = purchasePrice * Math.pow(1 + appreciationRate / 100, year);
        projectionData.push({
          name: `Year ${year}`,
          value: Math.round(value),
        });
      }

      return (
        <AdvancedCharts
          data={projectionData}
          type="line"
          title="Property Value Projection (10 Years)"
          dataKeys={['value']}
          colors={['#10b981']}
        />
      );
    }
  }

  // For cash flow calculators
  if (calculatorType.includes('cash-flow') || calculatorType.includes('rental')) {
    const monthlyRent = parseFloat(inputs.monthlyRent || inputs.rentalIncome || 0);
    const expenses = parseFloat(inputs.expenses || inputs.operatingExpenses || 0);

    if (monthlyRent || expenses) {
      const data = [
        { name: 'Monthly Rent', value: monthlyRent },
        { name: 'Expenses', value: expenses },
        { name: 'Net Income', value: Math.max(0, monthlyRent - expenses) },
      ].filter(item => item.value > 0);

      return (
        <AdvancedCharts
          data={data}
          type="bar"
          title="Monthly Income Breakdown"
          dataKeys={['value']}
          colors={['#3b82f6', '#ef4444', '#10b981']}
        />
      );
    }
  }

  return null;
};

