import { CalculatorContent } from '@/types';

export const PROPERTY_APPRECIATION_CALCULATOR_CONTENT: CalculatorContent = {
  title: 'Property Appreciation Calculator',
  description: 'Calculate property value growth over time and estimate future equity',
  icon: 'Icon',
  category: 'Real Estate',
  slug: 'property-appreciation-calculator',
  article: {
    title: "Understanding Property Appreciation",
    content: `
    <h2>What is Property Appreciation?</h2>
    <p>Property appreciation is the increase in a property's value over time. It's one of the primary ways real estate investors build wealth.</p>

    <h3>Types of Appreciation</h3>
    <ul>
      <li><strong>Market Appreciation:</strong> Natural increase due to supply/demand, location improvements, economic growth</li>
      <li><strong>Forced Appreciation:</strong> Value increase from renovations and improvements</li>
      <li><strong>Inflation-Driven:</strong> Appreciation that keeps pace with general price increases</li>
    </ul>

    <h3>Historical Appreciation Rates</h3>
    <p>U.S. residential real estate has historically appreciated at about 3-4% annually on average, though rates vary significantly by location and market conditions.</p>

    <h3>Factors Affecting Appreciation</h3>
    <ul>
      <li>Location and neighborhood development</li>
      <li>Local economic conditions and job growth</li>
      <li>Supply and demand dynamics</li>
      <li>Interest rates and financing availability</li>
      <li>Property condition and improvements</li>
      <li>School quality and local amenities</li>
    </ul>
  `,
  },
  calculator: {
    fields: [
      {
        name: "initialValue",
        label: "Initial Property Value",
        type: "number",
        placeholder: "500000",
        defaultValue: "500000",
      },
      {
        name: "annualRate",
        label: "Annual Appreciation Rate (%)",
        type: "number",
        placeholder: "3.5",
        defaultValue: "3.5",
      },
      {
        name: "years",
        label: "Number of Years",
        type: "number",
        placeholder: "10",
        defaultValue: "10",
      },
      {
        name: "downPayment",
        label: "Down Payment",
        type: "number",
        placeholder: "100000",
        defaultValue: "100000",
      },
    ],
    results: [
      { label: "Future Property Value", isCurrency: true },
      { label: "Total Appreciation", isCurrency: true },
      { label: "Return on Investment (ROI)", isCurrency: false },
      { label: "Average Annual Gain", isCurrency: true },
    ],
    calculate: (data: any) => {
      const initial = Number(data.initialValue) || 0;
      const rate = (Number(data.annualRate) || 0) / 100;
      const years = Number(data.years) || 0;
      const downPayment = Number(data.downPayment) || 0;

      const futureValue = initial * Math.pow(1 + rate, years);
      const totalAppreciation = futureValue - initial;
      const roi = downPayment > 0 ? (totalAppreciation / downPayment) * 100 : 0;
      const avgAnnualGain = years > 0 ? totalAppreciation / years : 0;

      return [
        { label: "Future Property Value", value: `${futureValue.toFixed(2)}`, isCurrency: true },
        { label: "Total Appreciation", value: `${totalAppreciation.toFixed(2)}`, isCurrency: true },
        { label: "Return on Investment (ROI)", value: `${roi.toFixed(2)}%`, isCurrency: false },
        { label: "Average Annual Gain", value: `${avgAnnualGain.toFixed(2)}`, isCurrency: true },
      ];
    },
  },
};

src/content/tools/down-payment-calculato.content:
import { CalculatorContent } from '@/types';

export const DOWN_PAYMENT_CALCULATOR_CONTENT: CalculatorContent = {
  title: 'Down Payment Calculator',
  description: 'Calculate down payment requirements and analyze different scenarios for home purchases',
  icon: 'Icon',
  category: 'Mortgage',
  slug: 'down-payment-calculator',
  article: {
    title: "Understanding Down Payments for Home Purchases",
    content: `
    <p>A down payment is the initial upfront portion of the total amount due on a home purchase. It's typically expressed as a percentage of the purchase price. The down payment amount directly affects your loan amount, monthly mortgage payment, and whether you'll need to pay private mortgage insurance (PMI).</p>

    <h2>How Much Should You Put Down?</h2>
    <p>While 20% is often cited as the ideal down payment, many buyers put down less. Here are common down payment scenarios:</p>
    <ul>
      <li><strong>3-5%:</strong> FHA loans and some conventional loans allow down payments as low as 3-5%</li>
      <li><strong>10%:</strong> A middle ground that reduces PMI costs while keeping cash reserves</li>
      <li><strong>20%:</strong> Avoids PMI entirely and typically gets better interest rates</li>
      <li><strong>25%+:</strong> Maximizes equity and minimizes monthly costs</li>
    </ul>

    <h2>Additional Costs to Consider</h2>
    <p>Beyond the down payment, homebuyers need to budget for:</p>
    <ul>
      <li><strong>Closing Costs:</strong> Typically 2-5% of the purchase price</li>
      <li><strong>PMI:</strong> Required if down payment is less than 20%</li>
      <li><strong>Property Taxes:</strong> Varies by location, usually 1-2% annually</li>
      <li><strong>Homeowners Insurance:</strong> Protects your investment</li>
      <li><strong>HOA Fees:</strong> If applicable to your property</li>
    </ul>

    <h2>Calculating Debt-to-Income Ratio</h2>
    <p>Lenders use your debt-to-income (DTI) ratio to determine loan eligibility:</p>
    <ul>
      <li><strong>Below 28%:</strong> Excellent - You're in a strong financial position</li>
      <li><strong>28-36%:</strong> Good - Most lenders approve loans in this range</li>
      <li><strong>Above 36%:</strong> Risky - May have difficulty qualifying for a loan</li>
    </ul>
  `,
  },
  calculator: {
    fields: [
      {
        name: "homePrice",
        label: "Home Price",
        type: "number",
        placeholder: "400000",
        defaultValue: "400000",
      },
      {
        name: "downPaymentPercent",
        label: "Down Payment (%)",
        type: "number",
        placeholder: "20",
        defaultValue: "20",
      },
      {
        name: "interestRate",
        label: "Interest Rate (%)",
        type: "number",
        placeholder: "6.5",
        defaultValue: "6.5",
      },
      {
        name: "loanTerm",
        label: "Loan Term (Years)",
        type: "number",
        placeholder: "30",
        defaultValue: "30",
      },
      {
        name: "monthlyIncome",
        label: "Monthly Gross Income",
        type: "number",
        placeholder: "8000",
        defaultValue: "8000",
      },
      {
        name: "monthlyDebts",
        label: "Monthly Debt Payments",
        type: "number",
        placeholder: "1200",
        defaultValue: "1200",
      },
      {
        name: "propertyTaxRate",
        label: "Property Tax Rate (%)",
        type: "number",
        placeholder: "1.2",
        defaultValue: "1.2",
      },
      {
        name: "homeInsurance",
        label: "Annual Home Insurance",
        type: "number",
        placeholder: "1200",
        defaultValue: "1200",
      },
      {
        name: "hoaFees",
        label: "Monthly HOA Fees",
        type: "number",
        placeholder: "200",
        defaultValue: "200",
      },
      {
        name: "closingCostPercent",
        label: "Closing Costs (%)",
        type: "number",
        placeholder: "3",
        defaultValue: "3",
      },
      {
        name: "pmiRate",
        label: "PMI Rate (%)",
        type: "number",
        placeholder: "0.5",
        defaultValue: "0.5",
      },
    ],
    results: [
      { label: "Down Payment Amount", isCurrency: true },
      { label: "Total Cash Needed at Closing", isCurrency: true },
      { label: "Loan Amount", isCurrency: true },
      { label: "Monthly Payment (PITI + PMI + HOA)", isCurrency: true },
      { label: "PMI Monthly Cost", isCurrency: true },
      { label: "Loan-to-Value Ratio", isCurrency: false },
      { label: "Debt-to-Income Ratio", isCurrency: false },
    ],
    calculate: (data: any) => {
      const {
        homePrice,
        downPaymentPercent,
        interestRate,
        loanTerm,
        monthlyIncome,
        monthlyDebts,
        propertyTaxRate,
        homeInsurance,
        hoaFees,
        closingCostPercent,
        pmiRate
      } = data;

      const price = Number(homePrice) || 0;
      const downPercent = Number(downPaymentPercent) || 0;
      const rate = (Number(interestRate) || 0) / 100 / 12;
      const term = (Number(loanTerm) || 0) * 12;
      const income = Number(monthlyIncome) || 0;
      const debts = Number(monthlyDebts) || 0;
      const taxRate = (Number(propertyTaxRate) || 0) / 100;
      const insurance = (Number(homeInsurance) || 0) / 12;
      const hoa = Number(hoaFees) || 0;
      const closingPercent = (Number(closingCostPercent) || 0) / 100;
      const pmi = (Number(pmiRate) || 0) / 100;

      const downPaymentAmount = price * (downPercent / 100);
      const loanAmount = price - downPaymentAmount;
      const loanToValue = (loanAmount / price) * 100;

      // Monthly payment calculation (P&I)
      let monthlyPayment = 0;
      if (rate > 0 && term > 0) {
        monthlyPayment = loanAmount * (rate * Math.pow(1 + rate, term)) / (Math.pow(1 + rate, term) - 1);
      }

      // PMI calculation (if down payment < 20%)
      const pmiAmount = downPercent < 20 ? (loanAmount * pmi / 12) : 0;

      // Property taxes
      const monthlyPropertyTax = price * taxRate / 12;

      // Total monthly payment (PITI + HOA + PMI)
      const totalMonthlyPayment = monthlyPayment + monthlyPropertyTax + insurance + hoa + pmiAmount;

      // Closing costs
      const totalClosingCosts = price * closingPercent;

      // Total cash needed
      const totalCashNeeded = downPaymentAmount + totalClosingCosts;

      // Debt-to-income ratio
      const debtToIncome = ((totalMonthlyPayment + debts) / income) * 100;

      return [
        { label: "Down Payment Amount", value: `${downPaymentAmount.toFixed(2)}`, isCurrency: true },
        { label: "Total Cash Needed at Closing", value: `${totalCashNeeded.toFixed(2)}`, isCurrency: true },
        { label: "Loan Amount", value: `${loanAmount.toFixed(2)}`, isCurrency: true },
        { label: "Monthly Payment (PITI + PMI + HOA)", value: `${totalMonthlyPayment.toFixed(2)}`, isCurrency: true },
        { label: "PMI Monthly Cost", value: `${pmiAmount.toFixed(2)}`, isCurrency: true },
        { label: "Loan-to-Value Ratio", value: `${loanToValue.toFixed(2)}%`, isCurrency: false },
        { label: "Debt-to-Income Ratio", value: `${debtToIncome.toFixed(2)}%`, isCurrency: false },
      ];
    },
  },
};

src/content/tools/loan-eligibility-calcu.content:
import { CalculatorContent } from '@/types';

export const LOAN_ELIGIBILITY_CALCULATOR_CONTENT: CalculatorContent = {
  title: 'Loan Eligibility Calculator',
  description: 'Determine if you qualify for a mortgage based on your income, debts, and credit profile',
  icon: 'Icon',
  category: 'Mortgage',
  slug: 'loan-eligibility-checker',
  article: {
    title: "Understanding Mortgage Loan Eligibility",
    content: `
    <p>Mortgage lenders use several factors to determine if you qualify for a home loan. Understanding these criteria can help you prepare before applying and improve your chances of approval.</p>

    <h2>Key Eligibility Factors</h2>
    <ul>
      <li><strong>Debt-to-Income Ratio (DTI):</strong> Most lenders prefer a DTI ratio below 43%, with many requiring 36% or less for conventional loans</li>
      <li><strong>Credit Score:</strong> Higher scores qualify for better rates; typically 620+ for conventional, 580+ for FHA</li>
      <li><strong>Employment History:</strong> Stable employment for at least 2 years is preferred</li>
      <li><strong>Down Payment:</strong> Minimum 3-20% depending on loan type</li>
      <li><strong>Loan-to-Value Ratio (LTV):</strong> Lower LTV ratios improve eligibility and rates</li>
    </ul>

    <h2>Types of Mortgage Loans</h2>
    <ul>
      <li><strong>Conventional:</strong> Requires good credit (620+) and typically 5-20% down</li>
      <li><strong>FHA:</strong> Lower credit requirements (580+), minimum 3.5% down</li>
      <li><strong>VA:</strong> No down payment for qualified veterans</li>
      <li><strong>USDA:</strong> No down payment for rural properties, income limits apply</li>
    </ul>

    <h2>Improving Your Eligibility</h2>
    <p>If you don't currently qualify, consider these steps:</p>
    <ul>
      <li>Pay down existing debts to lower your DTI ratio</li>
      <li>Improve your credit score by paying bills on time</li>
      <li>Save for a larger down payment</li>
      <li>Increase your income or add a co-borrower</li>
      <li>Reduce your target home price</li>
    </ul>
  `,
  },
  calculator: {
    fields: [
      {
        name: "annualIncome",
        label: "Annual Gross Income",
        type: "number",
        placeholder: "75000",
        defaultValue: "75000",
      },
      {
        name: "monthlyDebts",
        label: "Monthly Debt Payments",
        type: "number",
        placeholder: "800",
        defaultValue: "800",
      },
      {
        name: "creditScore",
        label: "Credit Score",
        type: "number",
        placeholder: "720",
        defaultValue: "720",
      },
      {
        name: "downPayment",
        label: "Down Payment Amount",
        type: "number",
        placeholder: "60000",
        defaultValue: "60000",
      },
      {
        name: "homePrice",
        label: "Target Home Price",
        type: "number",
        placeholder: "300000",
        defaultValue: "300000",
      },
      {
        name: "interestRate",
        label: "Expected Interest Rate (%)",
        type: "number",
        placeholder: "6.5",
        defaultValue: "6.5",
      },
      {
        name: "loanTerm",
        label: "Loan Term (Years)",
        type: "number",
        placeholder: "30",
        defaultValue: "30",
      },
      {
        name: "propertyTaxRate",
        label: "Property Tax Rate (% annually)",
        type: "number",
        placeholder: "1.2",
        defaultValue: "1.2",
      },
      {
        name: "homeInsurance",
        label: "Annual Home Insurance",
        type: "number",
        placeholder: "1200",
        defaultValue: "1200",
      },
    ],
    results: [
      { label: "Maximum Loan Amount You Qualify For", isCurrency: true },
      { label: "Maximum Home Price You Can Afford", isCurrency: true },
      { label: "Debt-to-Income Ratio", isCurrency: false },
      { label: "Loan-to-Value Ratio", isCurrency: false },
      { label: "Estimated Monthly Payment", isCurrency: true },
      { label: "Loan Eligibility Status", isCurrency: false },
    ],
    calculate: (data: any) => {
      const {
        annualIncome,
        monthlyDebts,
        creditScore,
        downPayment,
        homePrice,
        interestRate,
        loanTerm,
        propertyTaxRate,
        homeInsurance
      } = data;

      const monthlyIncome = (Number(annualIncome) || 0) / 12;
      const debts = Number(monthlyDebts) || 0;
      const credit = Number(creditScore) || 0;
      const down = Number(downPayment) || 0;
      const price = Number(homePrice) || 0;
      const rate = (Number(interestRate) || 0) / 100 / 12;
      const term = (Number(loanTerm) || 0) * 12;
      const taxRate = (Number(propertyTaxRate) || 0) / 100;
      const insurance = (Number(homeInsurance) || 0) / 12;

      // Calculate maximum DTI-based loan
      const maxHousingPayment = monthlyIncome * 0.28; // Front-end ratio
      const maxTotalDebt = monthlyIncome * 0.43; // Back-end ratio
      const maxHousingAfterDebts = maxTotalDebt - debts;
      const maxAllowedHousing = Math.min(maxHousingPayment, maxHousingAfterDebts);

      // Calculate how much principal & interest this allows
      const estimatedTaxAndInsurance = insurance + (price * taxRate / 12);
      const maxPIPayment = maxAllowedHousing - estimatedTaxAndInsurance;

      // Calculate maximum loan from P&I payment
      let maxLoanAmount = 0;
      if (rate > 0 && term > 0 && maxPIPayment > 0) {
        maxLoanAmount = maxPIPayment * ((Math.pow(1 + rate, term) - 1) / (rate * Math.pow(1 + rate, term)));
      }

      const maxHomePrice = maxLoanAmount + down;

      // Calculate actual loan details
      const loanAmount = price - down;
      const loanToValue = (loanAmount / price) * 100;

      // Calculate monthly payment for requested home
      let monthlyPI = 0;
      if (rate > 0 && term > 0) {
        monthlyPI = loanAmount * (rate * Math.pow(1 + rate, term)) / (Math.pow(1 + rate, term) - 1);
      }

      const monthlyTax = price * taxRate / 12;
      const totalMonthlyPayment = monthlyPI + monthlyTax + insurance;
      const totalDebtPayment = totalMonthlyPayment + debts;
      const dtiRatio = (totalDebtPayment / monthlyIncome) * 100;

      // Determine eligibility
      let eligibilityStatus = "";
      if (credit < 580) {
        eligibilityStatus = "Not Eligible - Credit score too low (minimum 580 required)";
      } else if (dtiRatio > 43) {
        eligibilityStatus = "Not Eligible - Debt-to-Income ratio too high (maximum 43%)";
      } else if (loanToValue > 97) {
        eligibilityStatus = "Not Eligible - Insufficient down payment";
      } else if (dtiRatio <= 28 && credit >= 740) {
        eligibilityStatus = "Excellent - Highly likely to qualify for best rates";
      } else if (dtiRatio <= 36 && credit >= 680) {
        eligibilityStatus = "Good - Likely to qualify with competitive rates";
      } else {
        eligibilityStatus = "Fair - May qualify but with higher rates";
      }

      return [
        { label: "Maximum Loan Amount You Qualify For", value: `${maxLoanAmount.toFixed(2)}`, isCurrency: true },
        { label: "Maximum Home Price You Can Afford", value: `${maxHomePrice.toFixed(2)}`, isCurrency: true },
        { label: "Debt-to-Income Ratio", value: `${dtiRatio.toFixed(2)}%`, isCurrency: false },
        { label: "Loan-to-Value Ratio", value: `${loanToValue.toFixed(2)}%`, isCurrency: false },
        { label: "Estimated Monthly Payment", value: `${totalMonthlyPayment.toFixed(2)}`, isCurrency: true },
        { label: "Loan Eligibility Status", value: eligibilityStatus, isCurrency: false },
      ];
    },
  },
};

src/content/tools/70-percent-rule-calcul.content:
import { CalculatorContent } from '@/types';

export const SEVENTY_PERCENT_RULE_CALCULATOR_CONTENT: CalculatorContent = {
  title: '70% Rule Calculator',
  description: 'Determine maximum purchase price for fix-and-flip properties using the 70% rule',
  icon: 'Icon',
  category: 'Real Estate',
  slug: '70-percent-rule-calculator',
  article: {
    title: "Understanding the 70% Rule",
    content: `
    <h2>Understanding the 70% Rule</h2>
    <p>The 70% rule is a guideline used by real estate investors, particularly those who fix and flip properties. It states that an investor should pay no more than 70% of the after-repair value (ARV) of a property, minus the cost of repairs.</p>

    <h3>How to Apply the 70% Rule</h3>
    <p>The formula is as follows:</p>
    <p><strong>Maximum Purchase Price = (ARV × 0.70) - Repair Costs</strong></p>
    <p>For example, if a property has an ARV of $300,000 and requires $40,000 in repairs, the maximum purchase price according to the 70% rule would be $170,000.</p>

    <h3>Why 70%?</h3>
    <p>The 30% margin is intended to cover:</p>
    <ul>
      <li><strong>Holding Costs:</strong> Mortgage payments, utilities, property taxes during renovation</li>
      <li><strong>Closing Costs:</strong> Both buying and selling costs (typically 2-5% each)</li>
      <li><strong>Financing Costs:</strong> Interest on loans, points paid</li>
      <li><strong>Profit Margin:</strong> Your compensation for time, effort, and risk</li>
      <li><strong>Unexpected Expenses:</strong> Buffer for surprises during renovation</li>
    </ul>

    <h3>Using the 70% Rule for Investment Analysis</h3>
    <p>This rule helps investors determine a reasonable offer price for a property that needs renovation. However, it's conservative and may need adjustment based on:</p>
    <ul>
      <li>Your market conditions (hot vs. cold markets)</li>
      <li>Your renovation efficiency and costs</li>
      <li>Your financing terms</li>
      <li>The scope of repairs needed</li>
    </ul>
  `,
  },
  calculator: {
    fields: [
      {
        name: "afterRepairValue",
        label: "After-Repair Value (ARV)",
        type: "number",
        placeholder: "300000",
        defaultValue: "300000",
      },
      {
        name: "repairCosts",
        label: "Estimated Repair Costs",
        type: "number",
        placeholder: "40000",
        defaultValue: "40000",
      },
    ],
    results: [
      { label: "Maximum Purchase Price", isCurrency: true },
      { label: "Total Investment (Purchase + Repairs)", isCurrency: true },
      { label: "Profit Margin (30% of ARV)", isCurrency: true },
    ],
    calculate: (data: any) => {
      const arv = Number(data.afterRepairValue) || 0;
      const repairCosts = Number(data.repairCosts) || 0;

      const maxPurchasePrice = (arv * 0.70) - repairCosts;
      const totalInvestment = maxPurchasePrice + repairCosts;
      const profitMargin = arv * 0.30;

      return [
        { label: "Maximum Purchase Price", value: `${maxPurchasePrice.toFixed(2)}`, isCurrency: true },
        { label: "Total Investment (Purchase + Repairs)", value: `${totalInvestment.toFixed(2)}`, isCurrency: true },
        { label: "Profit Margin (30% of ARV)", value: `${profitMargin.toFixed(2)}`, isCurrency: true },
      ];
    },
  },
};

src/content/tools/energy-savings-calcula.content:
import { CalculatorContent } from '@/types';

export const ENERGY_SAVINGS_CALCULATOR_CONTENT: CalculatorContent = {
  title: 'Energy Savings Calculator',
  description: 'Calculate potential energy savings from home improvements and payback period',
  icon: 'Icon',
  category: 'Home Improvement',
  slug: 'energy-savings-calculator',
  article: {
    title: "Understanding Home Energy Savings",
    content: `
    <h2>Energy-Efficient Home Improvements</h2>
    <p>Making energy-efficient improvements to your home can significantly reduce utility bills while increasing property value and comfort.</p>

    <h3>High-Impact Improvements</h3>
    <ul>
      <li><strong>Solar Panels:</strong> Can reduce electricity bills by 50-100%</li>
      <li><strong>Insulation:</strong> Reduces heating/cooling costs by 10-50%</li>
      <li><strong>Energy-Efficient HVAC:</strong> Saves 20-40% on heating/cooling</li>
      <li><strong>LED Lighting:</strong> Uses 75% less energy than incandescent</li>
      <li><strong>Energy-Efficient Windows:</strong> Reduces energy loss by 25-50%</li>
    </ul>

    <h3>Financial Benefits</h3>
    <ul>
      <li>Lower monthly utility bills</li>
      <li>Federal tax credits (up to 30% for solar)</li>
      <li>State and utility rebates</li>
      <li>Increased home value</li>
      <li>Lower carbon footprint</li>
    </ul>

    <h3>Calculating Payback Period</h3>
    <p><strong>Payback Period = Improvement Cost / Annual Savings</strong></p>
    <p>Most energy improvements pay for themselves in 5-15 years, while providing benefits for 20+ years.</p>
  `,
  },
  calculator: {
    fields: [
      {
        name: "currentMonthlyBill",
        label: "Current Monthly Energy Bill",
        type: "number",
        placeholder: "250",
        defaultValue: "250",
      },
      {
        name: "improvementCost",
        label: "Total Improvement Cost",
        type: "number",
        placeholder: "15000",
        defaultValue: "15000",
      },
      {
        name: "expectedSavingsPercent",
        label: "Expected Savings (%)",
        type: "number",
        placeholder: "30",
        defaultValue: "30",
      },
      {
        name: "federalTaxCredit",
        label: "Federal Tax Credit (%)",
        type: "number",
        placeholder: "30",
        defaultValue: "30",
      },
      {
        name: "utilityRebate",
        label: "Utility Rebate Amount",
        type: "number",
        placeholder: "1000",
        defaultValue: "1000",
      },
    ],
    results: [
      { label: "Monthly Energy Savings", isCurrency: true },
      { label: "Annual Energy Savings", isCurrency: true },
      { label: "Total Incentives & Rebates", isCurrency: true },
      { label: "Net Cost After Incentives", isCurrency: true },
      { label: "Payback Period (Years)", isCurrency: false },
      { label: "30-Year Total Savings", isCurrency: true },
    ],
    calculate: (data: any) => {
      const monthlyBill = Number(data.currentMonthlyBill) || 0;
      const improvementCost = Number(data.improvementCost) || 0;
      const savingsPercent = (Number(data.expectedSavingsPercent) || 0) / 100;
      const taxCreditPercent = (Number(data.federalTaxCredit) || 0) / 100;
      const rebate = Number(data.utilityRebate) || 0;

      const monthlySavings = monthlyBill * savingsPercent;
      const annualSavings = monthlySavings * 12;
      const federalCredit = improvementCost * taxCreditPercent;
      const totalIncentives = federalCredit + rebate;
      const netCost = improvementCost - totalIncentives;
      const paybackYears = annualSavings > 0 ? netCost / annualSavings : 0;
      const thirtYearSavings = (annualSavings * 30) - netCost;

      return [
        { label: "Monthly Energy Savings", value: `${monthlySavings.toFixed(2)}`, isCurrency: true },
        { label: "Annual Energy Savings", value: `${annualSavings.toFixed(2)}`, isCurrency: true },
        { label: "Total Incentives & Rebates", value: `${totalIncentives.toFixed(2)}`, isCurrency: true },
        { label: "Net Cost After Incentives", value: `${netCost.toFixed(2)}`, isCurrency: true },
        { label: "Payback Period (Years)", value: `${paybackYears.toFixed(1)}`, isCurrency: false },
        { label: "30-Year Total Savings", value: `${thirtYearSavings.toFixed(2)}`, isCurrency: true },
      ];
    },
  },
};

src/content/tools/depreciation-calculato.content:
import { CalculatorContent } from '@/types';

export const DEPRECIATION_CALCULATOR_CONTENT: CalculatorContent = {
  title: 'Depreciation Calculator',
  description: 'Calculate annual depreciation for rental properties and estimate tax savings',
  icon: 'Icon',
  category: 'Tax & Legal',
  slug: 'depreciation-calculator',
  article: {
    title: "Understanding Real Estate Depreciation",
    content: `
    <h2>What is Real Estate Depreciation?</h2>
    <p>Depreciation is a tax deduction that allows rental property owners to recover the cost of income-producing property over time. The IRS considers real estate as having a "useful life" and allows you to deduct a portion of the property's cost each year.</p>

    <h3>Depreciation Rules</h3>
    <ul>
      <li><strong>Residential Property:</strong> Depreciated over 27.5 years</li>
      <li><strong>Commercial Property:</strong> Depreciated over 39 years</li>
      <li><strong>Land:</strong> Cannot be depreciated (only the building)</li>
    </ul>

    <h3>How Depreciation Works</h3>
    <p><strong>Depreciable Basis = Purchase Price - Land Value + Improvements</strong></p>
    <p>This basis is divided by the depreciation period to get your annual deduction.</p>

    <h3>Tax Benefits</h3>
    <p>Depreciation is a "paper loss" that reduces taxable income without actual cash outflow, providing significant tax savings for rental property owners.</p>

    <h3>Depreciation Recapture</h3>
    <p>When you sell the property, the IRS "recaptures" depreciation taken, taxing it at up to 25%. This is an important consideration for long-term tax planning.</p>
  `,
  },
  calculator: {
    fields: [
      {
        name: "purchasePrice",
        label: "Purchase Price",
        type: "number",
        placeholder: "300000",
        defaultValue: "300000",
      },
      {
        name: "landValue",
        label: "Land Value",
        type: "number",
        placeholder: "60000",
        defaultValue: "60000",
      },
      {
        name: "propertyType",
        label: "Property Type (27.5 for residential, 39 for commercial)",
        type: "number",
        placeholder: "27.5",
        defaultValue: "27.5",
      },
      {
        name: "marginalTaxRate",
        label: "Marginal Tax Rate (%)",
        type: "number",
        placeholder: "24",
        defaultValue: "24",
      },
    ],
    results: [
      { label: "Depreciable Basis", isCurrency: true },
      { label: "Annual Depreciation", isCurrency: true },
      { label: "Annual Tax Savings", isCurrency: true },
      { label: "Total Depreciation Over Period", isCurrency: true },
    ],
    calculate: (data: any) => {
      const purchasePrice = Number(data.purchasePrice) || 0;
      const landValue = Number(data.landValue) || 0;
      const propertyType = Number(data.propertyType) || 27.5;
      const taxRate = (Number(data.marginalTaxRate) || 0) / 100;

      const depreciableBasis = purchasePrice - landValue;
      const annualDepreciation = depreciableBasis / propertyType;
      const annualTaxSavings = annualDepreciation * taxRate;
      const totalDepreciation = depreciableBasis;

      return [
        { label: "Depreciable Basis", value: `${depreciableBasis.toFixed(2)}`, isCurrency: true },
        { label: "Annual Depreciation", value: `${annualDepreciation.toFixed(2)}`, isCurrency: true },
        { label: "Annual Tax Savings", value: `${annualTaxSavings.toFixed(2)}`, isCurrency: true },
        { label: "Total Depreciation Over Period", value: `${totalDepreciation.toFixed(2)}`, isCurrency: true },
      ];
    },
  },
};

src/content/tools/hoa-fee-calculator.ts.content:
import { CalculatorContent } from '@/types';

export const HOA_FEE_CALCULATOR_CONTENT: CalculatorContent = {
  title: 'HOA Fee Calculator',
  description: 'Calculate total homeownership costs including HOA fees and assess affordability',
  icon: 'Icon',
  category: 'Real Estate',
  slug: 'hoa-fee-calculator',
  article: {
    title: "Understanding HOA Fees",
    content: `
    <h2>What are HOA Fees?</h2>
    <p>Homeowners Association (HOA) fees are monthly or annual charges paid by homeowners in planned communities, condominiums, or townhomes. These fees cover the cost of maintaining common areas and shared amenities.</p>

    <h3>What Do HOA Fees Cover?</h3>
    <ul>
      <li><strong>Common Area Maintenance:</strong> Landscaping, pool maintenance, clubhouse upkeep</li>
      <li><strong>Utilities:</strong> Water, trash collection, sometimes electricity for common areas</li>
      <li><strong>Insurance:</strong> Master insurance policy for the building or complex</li>
      <li><strong>Repairs & Reserves:</strong> Major repairs and reserve fund for future projects</li>
      <li><strong>Amenities:</strong> Gym, pool, tennis courts, security services</li>
      <li><strong>Professional Management:</strong> HOA management company fees</li>
    </ul>

    <h3>Impact on Your Budget</h3>
    <p>HOA fees significantly impact your monthly housing costs and should be factored into affordability calculations. Average HOA fees range from $200-$400/month but can be much higher for luxury properties or buildings with extensive amenities.</p>

    <h3>Things to Consider</h3>
    <ul>
      <li><strong>Special Assessments:</strong> One-time fees for major repairs not covered by reserves</li>
      <li><strong>Fee Increases:</strong> HOA fees typically increase 3-5% annually</li>
      <li><strong>Financial Health:</strong> Review HOA financial statements before buying</li>
      <li><strong>Rules & Restrictions:</strong> HOAs may have strict rules about renovations, rentals, pets</li>
    </ul>
  `,
  },
  calculator: {
    fields: [
      {
        name: "monthlyHOAFee",
        label: "Monthly HOA Fee",
        type: "number",
        placeholder: "300",
        defaultValue: "300",
      },
      {
        name: "homePrice",
        label: "Home Purchase Price",
        type: "number",
        placeholder: "400000",
        defaultValue: "400000",
      },
      {
        name: "downPayment",
        label: "Down Payment (%)",
        type: "number",
        placeholder: "20",
        defaultValue: "20",
      },
      {
        name: "interestRate",
        label: "Interest Rate (%)",
        type: "number",
        placeholder: "6.5",
        defaultValue: "6.5",
      },
      {
        name: "loanTerm",
        label: "Loan Term (Years)",
        type: "number",
        placeholder: "30",
        defaultValue: "30",
      },
      {
        name: "propertyTax",
        label: "Annual Property Tax",
        type: "number",
        placeholder: "4800",
        defaultValue: "4800",
      },
      {
        name: "homeInsurance",
        label: "Annual Home Insurance",
        type: "number",
        placeholder: "1200",
        defaultValue: "1200",
      },
    ],
    results: [
      { label: "Monthly HOA Fee", isCurrency: true },
      { label: "Annual HOA Cost", isCurrency: true },
      { label: "Monthly Mortgage Payment (P&I)", isCurrency: true },
      { label: "Total Monthly Housing Cost", isCurrency: true },
      { label: "HOA as % of Total Housing Cost", isCurrency: false },
    ],
    calculate: (data: any) => {
      const hoaFee = Number(data.monthlyHOAFee) || 0;
      const homePrice = Number(data.homePrice) || 0;
      const downPaymentPercent = Number(data.downPayment) || 0;
      const interestRate = (Number(data.interestRate) || 0) / 100 / 12;
      const loanTerm = (Number(data.loanTerm) || 0) * 12;
      const propertyTax = (Number(data.propertyTax) || 0) / 12;
      const homeInsurance = (Number(data.homeInsurance) || 0) / 12;

      const downPaymentAmount = homePrice * (downPaymentPercent / 100);
      const loanAmount = homePrice - downPaymentAmount;

      let monthlyPI = 0;
      if (interestRate > 0 && loanTerm > 0) {
        monthlyPI = loanAmount * (interestRate * Math.pow(1 + interestRate, loanTerm)) / (Math.pow(1 + interestRate, loanTerm) - 1);
      }

      const totalMonthly = monthlyPI + propertyTax + homeInsurance + hoaFee;
      const hoaPercentage = totalMonthly > 0 ? (hoaFee / totalMonthly) * 100 : 0;

      return [
        { label: "Monthly HOA Fee", value: `${hoaFee.toFixed(2)}`, isCurrency: true },
        { label: "Annual HOA Cost", value: `${(hoaFee * 12).toFixed(2)}`, isCurrency: true },
        { label: "Monthly Mortgage Payment (P&I)", value: `${monthlyPI.toFixed(2)}`, isCurrency: true },
        { label: "Total Monthly Housing Cost", value: `${totalMonthly.toFixed(2)}`, isCurrency: true },
        { label: "HOA as % of Total Housing Cost", value: `${hoaPercentage.toFixed(2)}%`, isCurrency: false },
      ];
    },
  },
};

src/content/tools/1-percent-rule-calcula.content:
import { CalculatorContent } from '@/types';

export const ONE_PERCENT_RULE_CALCULATOR_CONTENT: CalculatorContent = {
  title: '1% Rule Calculator',
  description: 'Quickly screen potential rental properties using the 1% rule guideline',
  icon: 'Icon',
  category: 'Real Estate',
  slug: '1-percent-rule-calculator',
  article: {
    title: "Understanding the 1% Rule",
    content: `
    <h2>Understanding the 1% Rule</h2>
    <p>The 1% rule is a guideline used by real estate investors to quickly screen potential investment properties. It states that the gross monthly rent should be at least 1% of the property's purchase price.</p>

    <h3>How to Apply the 1% Rule</h3>
    <p>The formula is simple:</p>
    <p><strong>Monthly Rent ≥ Purchase Price × 0.01</strong></p>
    <p>For example, if a property costs $200,000, the monthly rent should be at least $2,000 to meet the 1% rule.</p>

    <h3>Why Use the 1% Rule?</h3>
    <ul>
      <li><strong>Quick Screening:</strong> Helps filter properties quickly before detailed analysis</li>
      <li><strong>Cash Flow Indicator:</strong> Properties meeting the rule are more likely to generate positive cash flow</li>
      <li><strong>Market Comparison:</strong> Useful for comparing investment potential across markets</li>
    </ul>

    <h3>Limitations</h3>
    <p>The 1% rule is a rough guideline and doesn't account for:</p>
    <ul>
      <li>Operating expenses (taxes, insurance, maintenance)</li>
      <li>Vacancy rates</li>
      <li>Property management fees</li>
      <li>Local market conditions</li>
    </ul>
    <p>Use it as an initial filter, then perform detailed analysis on properties that meet the criteria.</p>
  `,
  },
  calculator: {
    fields: [
      {
        name: "purchasePrice",
        label: "Purchase Price",
        type: "number",
        placeholder: "200000",
        defaultValue: "200000",
      },
      {
        name: "monthlyRent",
        label: "Monthly Rent",
        type: "number",
        placeholder: "2000",
        defaultValue: "2000",
      },
    ],
    results: [
      { label: "1% Threshold", isCurrency: true },
      { label: "Rent as % of Purchase Price", isCurrency: false },
      { label: "Meets 1% Rule?", isCurrency: false },
    ],
    calculate: (data: any) => {
      const purchasePrice = Number(data.purchasePrice) || 0;
      const monthlyRent = Number(data.monthlyRent) || 0;

      const onePercentThreshold = purchasePrice * 0.01;
      const rentPercentage = purchasePrice > 0 ? (monthlyRent / purchasePrice) * 100 : 0;
      const meetsRule = monthlyRent >= onePercentThreshold ? "✅ Yes" : "❌ No";

      return [
        { label: "1% Threshold", value: `${onePercentThreshold.toFixed(2)}`, isCurrency: true },
        { label: "Rent as % of Purchase Price", value: `${rentPercentage.toFixed(2)}%`, isCurrency: false },
        { label: "Meets 1% Rule?", value: meetsRule, isCurrency: false },
      ];
    },
  },
};

